INSERT INTO `vaccine` (`mavc`, `tenvc`, `nuocsx`, `gia`, `soluong`, `thongtinvc`, `mancc`, `benh_nhombenh`, `solieu`, `khoangcachmuitiem`, `trangthaivc`, `hinhanhvc`) VALUES
('vcAdacel', 'Adacel', 'Canada', '775000', 64, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin Adacel tạo miễn dịch&nbsp;chủ động nhắc lại nhằm ph&ograve;ng bệnh ho g&agrave; &ndash; bạch hầu &ndash; uốn v&aacute;n.</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Adacel ngừa ho g&agrave; &ndash; bạch hầu &ndash; uốn v&aacute;n được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học&nbsp;<a href=\"https://www.sanofi.com/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Sanofi Pasteur</a>&nbsp;&ndash; Ph&aacute;p.&nbsp;Vắc xin Adacel được&nbsp;sản xuất tại Canada.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Ti&ecirc;m bắp.&nbsp;</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Người qu&aacute; mẫn với th&agrave;nh phần của vắc xin.</li>\r\n	<li>Bệnh l&yacute; n&atilde;o (v&iacute; dụ h&ocirc;n m&ecirc;, giảm tri gi&aacute;c, co giật k&eacute;o d&agrave;i) xảy ra trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m một liều vắc xin bất kỳ c&oacute; chứa th&agrave;nh phần ho g&agrave; m&agrave; kh&ocirc;ng x&aacute;c định được nguy&ecirc;n nh&acirc;n n&agrave;o kh&aacute;c.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Ho&atilde;n việc sử dụng vắc xin khi c&oacute; sốt cao hoặc c&aacute;c bệnh cấp t&iacute;nh.</li>\r\n	<li>Kh&ocirc;ng n&ecirc;n ti&ecirc;m bắp Adacel cho những người bị c&aacute;c rối loạn chảy m&aacute;u, như bệnh m&aacute;u kh&oacute; đ&ocirc;ng hoặc giảm tiểu cầu.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Đau tại nơi ti&ecirc;m l&agrave; phản ứng tại chỗ hay gặp nhất, mệt mỏi ở trẻ em v&agrave; nhức đầu ở thiếu ni&ecirc;n v&agrave; người lớn, sốt được b&aacute;o c&aacute;o ở dưới 10% số người được ti&ecirc;m vắc xin.</li>\r\n	<li>Phản ứng dị ứng qu&aacute; mẫn, ngứa, m&agrave;y đay (hiếm khi xảy ra).&nbsp;</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Khi ti&ecirc;m Adacel c&ugrave;ng với c&aacute;c vắc xin kh&aacute;c c&ugrave;ng buổi ti&ecirc;m chủng th&igrave; phải d&ugrave;ng những bơm ti&ecirc;m ri&ecirc;ng v&agrave; ti&ecirc;m ở những vị tr&iacute; kh&aacute;c nhau v&agrave; tốt nhất l&agrave; n&ecirc;n ti&ecirc;m ở c&aacute;c chi kh&aacute;c nhau.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin được bảo quản ở nhiệt độ từ +2 đến +8 độ C. Kh&ocirc;ng được để đ&ocirc;ng băng.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Adacel được chỉ định cho trẻ từ 4 tuổi trở l&ecirc;n v&agrave; người lớn đến 64 tuổi.</p>\r\n\r\n<h2>3. Phác đ&ocirc;̀, lịch ti&ecirc;m</h2>\r\n\r\n<p>Vắc xin 3 trong 1 Adacel (Canada) c&oacute; lịch ti&ecirc;m như sau:</p>\r\n\r\n<p><em><strong>Trẻ từ 4 tuổi trở l&ecirc;n v&agrave; người lớn đ&atilde; ti&ecirc;m lịch cơ bản bạch hầu &ndash; ho g&agrave; &ndash; uốn v&aacute;n:</strong></em><br />\r\nLịch ti&ecirc;m 1 mũi<br />\r\n&bull; Ti&ecirc;m nhắc 1 mũi mỗi 10 năm hoặc ti&ecirc;m nhắc theo độ tuổi khuyến c&aacute;o</p>\r\n\r\n<p><em><strong>Trẻ từ 4 tuổi trở l&ecirc;n v&agrave; người lớn chưa ti&ecirc;m đủ lịch cơ bản bạch hầu &ndash; ho g&agrave; &ndash; uốn v&aacute;n:</strong></em><br />\r\nLịch ti&ecirc;m 3 mũi:<br />\r\n&bull; Mũi 1: Lần ti&ecirc;m đầu ti&ecirc;n<br />\r\n&bull; Mũi 2: Một th&aacute;ng sau mũi 1<br />\r\n&bull; Mũi 3: S&aacute;u th&aacute;ng sau mũi 2</p>\r\n\r\n<p>&bull; Ti&ecirc;m nhắc một mũi mỗi 10 năm hoặc ti&ecirc;m nhắc theo độ tuổi khuyến c&aacute;o.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong><br />\r\nC&oacute; thể chuyển đổi giữa Adacel v&agrave; Boostrix ở những liều ti&ecirc;m nhắc.</p>\r\n\r\n<h2>4. Phụ nữ mang thai</h2>\r\n\r\n<p>Adacel c&oacute; thể được sử dụng cho phụ nữ mang thai trong ba th&aacute;ng giữa hoặc ba th&aacute;ng cuối thai kỳ để cung cấp sự bảo vệ chủ động cho trẻ ph&ograve;ng bệnh ho g&agrave;.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p><strong><em>Sau ti&ecirc;m vắc xin 3 trong 1 Adacel c&oacute; biểu hiện sốt kh&ocirc;ng?</em></strong></p>\r\n\r\n<p>Sau khi ti&ecirc;m 3 trong 1, người ti&ecirc;m hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể, cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đau, sưng, đỏ</li>\r\n	<li>To&agrave;n th&acirc;n: sốt, mệt mỏi, đau đầu, rối loạn ti&ecirc;u h&oacute;a</li>\r\n</ul>', 'ncc6', '7', 3, '60', 1, 'images/hinhanhvaccine/20240626_162526.jpg'),
('vcGCFLUQuadrivalent', 'GCFLU Quadrivalent', 'Hàn Quốc', '700000', 67, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin GCFlu Quadrivalent được chỉ định để ph&ograve;ng ngừa bệnh c&uacute;m m&ugrave;a do virus c&uacute;m thuộc 2 chủng c&uacute;m A (H1N1, H3N2) v&agrave; 2 chủng c&uacute;m B (Victoria v&agrave; Yamagata).</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin GC Flu Quadrivalent được nghi&ecirc;n cứu v&agrave; sản xuất bởi&nbsp;<a href=\"https://greencross.com.ph/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Green Cross</a>&nbsp;(H&agrave;n Quốc).</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Đường d&ugrave;ng: Ti&ecirc;m bắp.</p>\r\n\r\n<p>Ở trẻ 6-35 th&aacute;ng: vị tr&iacute; th&iacute;ch hợp để ti&ecirc;m bắp l&agrave; mặt trước b&ecirc;n của đ&ugrave;i (hoặc cơ delta của v&ugrave;ng tr&ecirc;n c&aacute;nh tay nếu khối lượng cơ bắp đủ). Ở trẻ em từ 36 th&aacute;ng tuổi v&agrave; người lớn: cơ delta của v&ugrave;ng tr&ecirc;n c&aacute;nh tay.</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Bệnh nh&acirc;n sốt hoặc người bị suy dinh dưỡng.</li>\r\n	<li>Bệnh nh&acirc;n bị rối loạn tim mạch, rối loạn thận hoặc bệnh gan trong khi bệnh đang trong giai đoạn cấp t&iacute;nh, hoặc hoạt động.</li>\r\n	<li>Bệnh nh&acirc;n mắc bệnh h&ocirc; hấp cấp t&iacute;nh hoặc bệnh truyền nhiễm t&iacute;ch cực kh&aacute;c.</li>\r\n	<li>Bệnh nh&acirc;n mắc bệnh thể ẩn v&agrave; trong giai đoạn dưỡng bệnh.</li>\r\n	<li>Người qu&aacute; mẫn với c&aacute;c th&agrave;nh phần của vắc xin.</li>\r\n	<li>Người bị dị ứng với trứng, thịt g&agrave;, mọi sản phẩm từ thịt g&agrave;.</li>\r\n	<li>Người bị sốt trong v&ograve;ng 2 ng&agrave;y hoặc c&oacute; triệu chứng dị ứng như ph&aacute;t ban to&agrave;n th&acirc;n sau ti&ecirc;m tại lần ti&ecirc;m ph&ograve;ng trước.</li>\r\n	<li>Người c&oacute; triệu chứng co giật trong v&ograve;ng 1 năm trước khi ti&ecirc;m chủng.</li>\r\n	<li>Người c&oacute; hội chứng Guillain-Barre hoặc người bị rối loạn thần kinh trong v&ograve;ng 6 tuần kể từ lần chủng ngừa c&uacute;m trước.</li>\r\n	<li>Người được chẩn đo&aacute;n mắc bệnh suy giảm miễn dịch.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Người ti&ecirc;m vắc xin n&ecirc;n giữ trạng th&aacute;i ổn định về t&acirc;m l&yacute;, giữ cho vị tr&iacute; ti&ecirc;m sạch sẽ, v&agrave; khi c&oacute; c&aacute;c triệu chứng sốt cao, co giật cần li&ecirc;n hệ ngay với b&aacute;c sĩ.</li>\r\n	<li>Cũng như với tất cả c&aacute;c vắc xin ti&ecirc;m, điều trị nội khoa th&iacute;ch hợp v&agrave; việc gi&aacute;m s&aacute;t phải lu&ocirc;n lu&ocirc;n sẵn s&agrave;ng trong trường hợp xảy ra phản ứng qu&aacute; mẫn sau khi ti&ecirc;m vắc xin.</li>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m tĩnh mạch trong bất kỳ trường hợp n&agrave;o.</li>\r\n	<li>Đ&aacute;p ứng kh&aacute;ng thể c&oacute; thể kh&ocirc;ng ho&agrave;n thiện ở những bệnh nh&acirc;n mắc chứng thiếu hụt miễn dịch bẩm sinh hoặc do điều trị.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>C&aacute;c phản ứng tại chỗ: đỏ, đau, sưng, cứng vị tr&iacute; ti&ecirc;m</li>\r\n	<li>C&aacute;c phản ứng to&agrave;n th&acirc;n: sốt, kh&oacute; chịu, đầu đầu, đổ mồ h&ocirc;i, chứng đau cơ, đau khớp</li>\r\n	<li>C&aacute;c phản ứng bất lợi n&agrave;y thường mất đi trong v&ograve;ng 1-2 ng&agrave;y m&agrave; kh&ocirc;ng cần điều trị.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin c&uacute;m GC Flu Quadrivalent phải được bảo quản tại 2&deg;C đến 8&deg;C (trong tủ lạnh).</li>\r\n	<li>Kh&ocirc;ng được đ&ocirc;ng băng.</li>\r\n	<li>Bảo quản trong bao b&igrave; gốc. Tr&aacute;nh tiếp x&uacute;c &aacute;nh s&aacute;ng.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>GC FLU Quadrivalent (H&agrave;n Quốc) d&agrave;nh cho trẻ từ 6 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người lớn.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p>Vắc xin GC FLU Quadrivalent (H&agrave;n Quốc) ph&ograve;ng bệnh c&uacute;m d&agrave;nh cho trẻ từ 6 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người lớn c&oacute; lịch ti&ecirc;m như sau:</p>\r\n\r\n<ul>\r\n	<li><em>Trẻ từ 6 th&aacute;ng đến 9 tuổi:</em>&nbsp;</li>\r\n</ul>\r\n\r\n<p>Lịch ti&ecirc;m 2 mũi:</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n</li>\r\n	<li>Mũi 2: một th&aacute;ng sau mũi 1</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><em>Trẻ từ 9 tuổi trở l&ecirc;n v&agrave; người lớn:&nbsp;</em>Lịch ti&ecirc;m 01 mũi duy nhất v&agrave; nhắc lại hằng năm.</li>\r\n</ul>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;Tổ chức Y tế Thế giới (WHO) khuyến c&aacute;o chỉ cần ti&ecirc;m nhắc lại vắc xin C&uacute;m h&agrave;ng năm , lu&ocirc;n c&aacute;ch tối thiểu 12 th&aacute;ng so với mũi trước.</p>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<ul>\r\n	<li><strong><em>Bị nhiễm Covid-19 rồi c&oacute; ti&ecirc;m c&uacute;m được kh&ocirc;ng?</em></strong></li>\r\n</ul>\r\n\r\n<p>Sau khi điều trị xong bệnh Covid-19, sức khỏe ho&agrave;n to&agrave;n ổn định (kh&ocirc;ng c&ograve;n c&aacute;c triệu chứng nghi ngờ mắc bệnh Covid-19) vẫn c&oacute; thể ti&ecirc;m c&aacute;c vắc xin kh&aacute;c được (trừ vắc xin Covid-19 cần chờ sau 6 th&aacute;ng). T&ugrave;y v&agrave;o sức khỏe kh&aacute;ch h&agrave;ng m&agrave; b&aacute;c sĩ kh&aacute;m s&agrave;ng lọc v&agrave; chỉ định ph&ugrave; hợp.</p>\r\n\r\n<ul>\r\n	<li><strong><em>Kh&aacute;ch h&agrave;ng trước khi ti&ecirc;m vắc xin c&uacute;m c&oacute; cần ăn thử trứng g&agrave; trước khi ti&ecirc;m để test dị ứng kh&ocirc;ng?</em></strong></li>\r\n</ul>\r\n\r\n<p>C&aacute;c khuyến c&aacute;o gần đ&acirc;y khẳng định, người dị ứng với trứng vẫn c&oacute; thể ti&ecirc;m ngừa c&uacute;m. V&igrave; vậy trong thực h&agrave;nh ti&ecirc;m chủng, b&aacute;c sĩ kh&ocirc;ng nhất thiết phải y&ecirc;u cầu người đi ti&ecirc;m vắc xin c&uacute;m cần ăn thử trứng để thử dị ứng trước khi ti&ecirc;m.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, b&ecirc;n cạnh việc kh&aacute;m s&agrave;ng lọc v&agrave; tư vấn của b&aacute;c sĩ, người đi ti&ecirc;m vắc xin n&ecirc;n th&ocirc;ng b&aacute;o cho b&aacute;c sĩ c&aacute;c th&ocirc;ng tin lịch sử dị ứng của bản th&acirc;n bao gồm dị ứng thức ăn, vắc xin, h&oacute;a chất, phản ứng nặng sau ti&ecirc;m lần trước (nếu c&oacute;)&hellip; Do vậy, kh&aacute;ch h&agrave;ng ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m đưa con đến&nbsp;<a href=\"https://vnvc.vn/\" rel=\"noopener\" target=\"_blank\">VNVC</a>&nbsp;để ti&ecirc;m chủng theo đ&uacute;ng lịch.&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong><em>Phụ nữ mang thai c&oacute; ti&ecirc;m được vắc xin c&uacute;m kh&ocirc;ng?</em></strong></li>\r\n</ul>\r\n\r\n<p>Vắc xin ph&ograve;ng c&uacute;m kh&ocirc;ng chống chỉ định với phụ nữ đang mang thai. Kh&aacute;ch h&agrave;ng c&oacute; thể ti&ecirc;m sau 3 th&aacute;ng đầu thai kỳ v&agrave; trước ng&agrave;y dự sinh &iacute;t nhất 1 th&aacute;ng. Khi ti&ecirc;m, b&aacute;c sĩ sẽ thăm kh&aacute;m, tư vấn, chỉ định mũi ti&ecirc;m ph&ugrave; hợp. Kh&aacute;ch h&agrave;ng cần ho&agrave;n tất một số thủ tục trước ti&ecirc;m.</p>\r\n\r\n<p>Lưu &yacute;: Vắc xin ph&ograve;ng C&uacute;m Ivacflu &ndash; S (Việt Nam) kh&ocirc;ng chỉ định cho phụ nữ mang thai.</p>\r\n\r\n<ul>\r\n	<li><strong><em>Phụ nữ đang cho con b&uacute; c&oacute; ti&ecirc;m được vắc xin ph&ograve;ng c&uacute;m kh&ocirc;ng?</em></strong></li>\r\n</ul>\r\n\r\n<p>Trong thời gian cho con b&uacute;, phụ nữ vẫn c&oacute; thể đi ti&ecirc;m chủng một số loại vắc xin như C&uacute;m tr&aacute;nh trở th&agrave;nh nguồn l&acirc;y nhiễm bệnh cho con, đặc biệt khi trẻ chưa đủ tuổi để ti&ecirc;m ngừa một số vắc xin.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Vắc xin GCFlu Quadrivalent l&agrave; vắc xin c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, sau khi ti&ecirc;m vắc xin ph&ograve;ng bệnh c&uacute;m, người ti&ecirc;m hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể. Cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đau, đỏ, sưng</li>\r\n	<li>To&agrave;n th&acirc;n: sốt,mệt mỏi, đau đầu</li>\r\n</ul>', 'ncc6', '12', 2, '30', 1, 'images/hinhanhvaccine/20240626_163444.jpg'),
('vcGeneHbvax', 'Gene Hbvax', 'Việt Nam', '220000', 60, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p><strong>Vắc xin ph&ograve;ng vi&ecirc;m gan B t&aacute;i tổ hợp Gene-HBvax</strong>&nbsp;ph&ograve;ng bệnh do virus vi&ecirc;m gan B &ndash; loại virus n&agrave;y c&oacute; thể l&acirc;y truyền qua đường m&aacute;u, qua quan hệ t&igrave;nh dục v&agrave; từ mẹ truyền sang con với khả năng l&acirc;y nhiễm cao gấp 10 lần so với virus HIV. Một khi nhiễm virus vi&ecirc;m gan B người bệnh phải chấp nhận sống chung với bệnh suốt đời. 25% trong bệnh nh&acirc;n vi&ecirc;m gan si&ecirc;u vi B sẽ ph&aacute;t sinh những vấn đề nghi&ecirc;m trọng về gan, kể cả ung thư gan.</p>\r\n\r\n<h3>Nguồn gốc</h3>\r\n\r\n<p>Vắc xin ph&ograve;ng vi&ecirc;m gan B t&aacute;i tổ hợp Gene-HBvax được sản xuất bởi&nbsp;<a href=\"https://www.vabiotech.com.vn/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">C&ocirc;ng ty TNHH MTV Vắc xin v&agrave; Sinh phẩm số 1 VABIOTECH (Việt Nam)</a>.</p>\r\n\r\n<h3>Đường ti&ecirc;m</h3>\r\n\r\n<p>Kh&ocirc;ng được ti&ecirc;m đường tĩnh mạch hoặc trong da,&nbsp;<strong>vắc xin Gene-HBvax</strong>&nbsp;được chỉ định ti&ecirc;m bắp. Ở người lớn th&igrave; ti&ecirc;m vắc xin v&agrave;o v&ugrave;ng cơ delta, song ở trẻ sơ sinh v&agrave; trẻ nhỏ th&igrave; n&ecirc;n ti&ecirc;m v&agrave;o v&ugrave;ng đ&ugrave;i ngo&agrave;i th&igrave; tốt hơn v&igrave; cơ delta c&ograve;n nhỏ. Ngoại lệ c&oacute; thể ti&ecirc;m vắc xin theo đường dưới da cho những bệnh nh&acirc;n ưa chảy m&aacute;u. Lắc kỹ lọ vắc xin trước khi ti&ecirc;m.</p>\r\n\r\n<h3>Chống chỉ định</h3>\r\n\r\n<ul>\r\n	<li>Những người mẫn cảm với bất cứ th&agrave;nh phần n&agrave;o của vắc xin;</li>\r\n	<li>Mắc c&aacute;c bệnh bẩm sinh;</li>\r\n	<li>Mệt mỏi, sốt cao hoặc phản ứng to&agrave;n th&acirc;n với bất kỳ một bệnh nhiễm tr&ugrave;ng đang tiến triển;</li>\r\n	<li>Bệnh tim, bệnh thận hoặc bệnh gan;</li>\r\n	<li>Bệnh tiểu đường;</li>\r\n	<li>Bệnh ung thư m&aacute;u v&agrave; c&aacute;c bệnh &aacute;c t&iacute;nh n&oacute;i chung.</li>\r\n</ul>\r\n\r\n<h3>Thận trọng khi sử dụng</h3>\r\n\r\n<h4>Thận trọng</h4>\r\n\r\n<p>Cần c&oacute; sẵn epinephrine để sử dụng trong c&aacute;c trường hợp hy hữu c&oacute; sốc phản vệ c&oacute; thể xảy ra.</p>\r\n\r\n<p>Đối với phụ nữ c&oacute; thai v&agrave; đang cho con b&uacute;</p>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng n&ecirc;n ti&ecirc;m cho phụ nữ c&oacute; thai. Tuy nhi&ecirc;n, những phụ nữ c&oacute; thai c&oacute; nguy cơ cao bị nhiễm vi r&uacute;t vi&ecirc;m gan B cũng c&oacute; thể ti&ecirc;m được vắc xin n&agrave;y.</li>\r\n	<li>Kh&ocirc;ng chỉ định ti&ecirc;m cho phụ nữ đang cho con b&uacute;.</li>\r\n</ul>\r\n\r\n<h4>Lưu &yacute;</h4>\r\n\r\n<ul>\r\n	<li>Thời kỳ ủ bệnh của vi&ecirc;m gan B kh&aacute; d&agrave;i, v&igrave; vậy c&oacute; thể người ti&ecirc;m đ&atilde; nhiễm vi&ecirc;m gan B v&agrave;o thời điểm ti&ecirc;m, m&agrave; chưa c&oacute; biểu hiện l&acirc;m s&agrave;ng. Vắc xin kh&ocirc;ng thể bảo vệ được người ti&ecirc;m trong trường hợp n&agrave;y.</li>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m Gene-HBvax v&agrave;o v&ugrave;ng m&ocirc;ng hoặc trong da v&igrave; c&aacute;c đường ti&ecirc;m n&agrave;y kh&ocirc;ng cho kết quả đ&aacute;p ứng miễn dịch tối ưu.</li>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m vắc xin n&agrave;y v&agrave;o tĩnh mạch.</li>\r\n	<li>Ở những bệnh nh&acirc;n thẩm t&aacute;ch m&aacute;u v&agrave; đối tượng c&oacute; hệ miễn dịch kh&ocirc;ng c&acirc;n bằng c&oacute; thể phải ti&ecirc;m c&aacute;c mũi bổ sung.</li>\r\n</ul>\r\n\r\n<h3>Tương t&aacute;c thuốc</h3>\r\n\r\n<p>Gene-HBvax c&oacute; thể ti&ecirc;m c&ugrave;ng globulin miễn dịch kh&aacute;ng virus vi&ecirc;m gan B nhưng ở những vị tr&iacute; kh&aacute;c nhau. Globulin miễn dịch phải ti&ecirc;m c&ugrave;ng vi&ecirc;m gan B trong những trường hợp sau:</p>\r\n\r\n<ul>\r\n	<li>Nguy cơ nhiễm m&aacute;u c&oacute; virus vi&ecirc;m gan B (kim ti&ecirc;m, dao,&hellip;)</li>\r\n	<li>Những b&agrave; mẹ mang HBsAg v&agrave; HBeAg dương t&iacute;nh n&ecirc;n ti&ecirc;m cho con (tốt nhất trong v&ograve;ng 24h đầu sau sinh).</li>\r\n</ul>\r\n\r\n<p>Gene-HBvax c&oacute; thể ti&ecirc;m c&ugrave;ng c&aacute;c loại vắc xin kh&aacute;c như: Bạch hầu &ndash; Ho g&agrave; &ndash; Uốn v&aacute;n, bại liệt, BCG m&agrave; kh&ocirc;ng l&agrave;m ảnh hưởng đến đ&aacute;p ứng miễn dịch của c&aacute;c loại vắc xin n&agrave;y. Song phải sử dụng bơm kim ti&ecirc;m kh&aacute;c nhau v&agrave; ti&ecirc;m v&agrave;o những vị tr&iacute; kh&aacute;c nhau.</p>\r\n\r\n<p>Nếu đang sử dụng bất kỳ loại thuốc điều trị n&agrave;o, cần tư vấn của b&aacute;c sĩ.</p>\r\n\r\n<h3>T&aacute;c dụng kh&ocirc;ng mong muốn</h3>\r\n\r\n<ul>\r\n	<li>Những phản ứng thường hay gặp nhất l&agrave; đau, sưng v&agrave; ban đỏ tại chỗ ti&ecirc;m. Những phản ứng n&agrave;y thường l&agrave; nhẹ v&agrave; sẽ hết trong v&ograve;ng hai ng&agrave;y sau ti&ecirc;m.</li>\r\n	<li>Những phản ứng to&agrave;n th&acirc;n &iacute;t gặp như sốt, đau đầu, buồn n&ocirc;n, ch&oacute;ng mặt v&agrave; mệt mỏi cũng c&oacute; thể xảy ra ở một v&agrave;i người sau khi ti&ecirc;m, song nguy&ecirc;n nh&acirc;n để n&oacute;i l&agrave; c&oacute; li&ecirc;n quan đến vắc xin hay kh&ocirc;ng th&igrave; vẫn chưa được x&aacute;c định.</li>\r\n</ul>\r\n\r\n<h3>Bảo quản</h3>\r\n\r\n<p>Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8 độ C). Kh&ocirc;ng được đ&oacute;ng băng.</p>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Gene-HBvax được chỉ định ph&ograve;ng&nbsp;<strong><a href=\"https://vnvc.vn/nguyen-nhan-dau-hieu-cach-phong-ngua-viem-gan-b/\" rel=\"noopener\" target=\"_blank\">vi&ecirc;m gan B</a></strong>&nbsp;cho trẻ sơ sinh v&agrave; người lớn.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<ul>\r\n	<li>Trẻ sơ sinh v&agrave; trẻ em đến tr&ograve;n 10 tuổi: liều 10mcg/0.5ml</li>\r\n	<li>Trẻ lớn v&agrave; người lớn tr&ecirc;n 10 tuổi (10 tuổi 1 ng&agrave;y): 20mcg/1ml</li>\r\n</ul>\r\n\r\n<p>Khoảng c&aacute;ch giữa c&aacute;c mũi ti&ecirc;m c&aacute;ch nhau tối thiểu l&agrave; 4 tuần. Một lịch ti&ecirc;m cơ bản gồm 3 liều ti&ecirc;m bắp như sau:</p>\r\n\r\n<h3><strong>Lịch ti&ecirc;m 0-1-2-12 th&aacute;ng:</strong></h3>\r\n\r\n<ul>\r\n	<li>Mũi đầu ti&ecirc;n: lần ti&ecirc;m đầu ti&ecirc;n. Đối với trẻ sơ sinh, tốt nhất ti&ecirc;m trong v&ograve;ng 24 giờ đầu sau sinh.</li>\r\n	<li>Mũi thứ hai: 1 th&aacute;ng sau mũi đầu ti&ecirc;n</li>\r\n	<li>Mũi thứ ba: 2 th&aacute;ng sau mũi đầu ti&ecirc;n</li>\r\n	<li>Mũi ti&ecirc;m nhắc lại: 1 năm sau mũi đầu ti&ecirc;n.</li>\r\n</ul>\r\n\r\n<h3><strong>Lịch ti&ecirc;m 0-1-6 th&aacute;ng:</strong></h3>\r\n\r\n<ul>\r\n	<li>Mũi đầu ti&ecirc;n: lần ti&ecirc;m đầu ti&ecirc;n. Đối với trẻ sơ sinh, tốt nhất ti&ecirc;m trong v&ograve;ng 24 giờ đầu sau sinh</li>\r\n	<li>Mũi ti&ecirc;m thứ hai: 1 th&aacute;ng sau mũi ti&ecirc;m đầu</li>\r\n	<li>Mũi ti&ecirc;m thứ ba: 6 th&aacute;ng sau mũi ti&ecirc;m đầu</li>\r\n	<li>Mũi ti&ecirc;m nhắc lại: 5 năm sau mũi ti&ecirc;m đầu</li>\r\n</ul>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<ul>\r\n	<li><strong>Phản ứng tại chỗ:</strong>&nbsp;đau, sưng v&agrave; ban đỏ tại chỗ ti&ecirc;m.</li>\r\n	<li><strong>To&agrave;n th&acirc;n:</strong>&nbsp;sốt, đau đầu, buồn n&ocirc;n, ch&oacute;ng mặt v&agrave; mệt mỏi.</li>\r\n</ul>', 'ncc6', '3', 2, '30', 1, 'images/hinhanhvaccine/20240626_161441.jpg'),
('vcHexaxim', 'Hexaxim', 'Pháp', '1048000', 13, '<p>Vắc xin Hexaxim l&agrave; vắc xin kết hợp ph&ograve;ng được 6 loại bệnh trong 1 mũi ti&ecirc;m, bao gồm: Ho g&agrave;, bạch hầu, uốn v&aacute;n, bại liệt, vi&ecirc;m gan B v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o mủ do H.Influenzae t&yacute;p B (Hib). T&iacute;ch hợp trong duy nhất trong 1 vắc xin, 6 trong 1 Hexaxim gi&uacute;p giảm số mũi ti&ecirc;m, đồng nghĩa với việc hạn chế đau đớn cho b&eacute; khi phải ti&ecirc;m qu&aacute; nhiều.&nbsp;</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Hexaxim được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học Sanofi Pasteur (Ph&aacute;p).</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<ul>\r\n	<li>Hexaxim được chỉ định ti&ecirc;m bắp. Vị tr&iacute; ti&ecirc;m l&agrave; mặt trước &ndash; ngo&agrave;i của phần tr&ecirc;n đ&ugrave;i v&agrave; v&ugrave;ng cơ delta ở trẻ 15 th&aacute;ng tuổi trở l&ecirc;n.</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Tiền sử trước đ&acirc;y bị phản ứng phản vệ sau khi ti&ecirc;m Hexaxim.</li>\r\n	<li>Qu&aacute; mẫn với c&aacute;c hoạt chất hay bất cứ t&aacute; dược n&agrave;o được liệt k&ecirc; trong th&agrave;nh phần của vắc xin, với c&aacute;c dư lượng vết (glutaraldehyde (<a href=\"https://www.cdc.gov/niosh/topics/glutaraldehyde/default.html\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">1</a>), formaldehyde (<a href=\"https://www.cancer.org/cancer/cancer-causes/formaldehyde.html\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">2</a>), neomycin, streptomycin, polymyxin B), với vắc xin ho g&agrave; bất kỳ, hoặc trước đ&acirc;y từng bị phản ứng qu&aacute; mẫn sau khi ti&ecirc;m Hexaxim hoặc sau khi ti&ecirc;m vắc xin chứa c&aacute;c th&agrave;nh phần tương tự .</li>\r\n	<li>Đối tượng c&oacute; bệnh l&yacute; n&atilde;o (tổn thương ở n&atilde;o) kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, xảy ra trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m 1 vắc xin chứa th&agrave;nh phần ho g&agrave; (vắc xin ho g&agrave; v&ocirc; b&agrave;o hay nguy&ecirc;n b&agrave;o). Trong trường hợp n&agrave;y, n&ecirc;n ngừng ti&ecirc;m vắc xin ho g&agrave; v&agrave; c&oacute; thể tiếp tục với qu&aacute; tr&igrave;nh ti&ecirc;m chủng với c&aacute;c vắc xin Bạch hầu, Uốn v&aacute;n, Vi&ecirc;m gan B, Bại liệt v&agrave; Hib.</li>\r\n	<li>Kh&ocirc;ng n&ecirc;n ti&ecirc;m vắc xin ho g&agrave; cho người c&oacute; rối loạn thần kinh kh&ocirc;ng kiểm so&aacute;t hoặc động kinh kh&ocirc;ng kiểm so&aacute;t cho đến khi bệnh được điều trị, bệnh ổn định v&agrave; lợi &iacute;ch r&otilde; r&agrave;ng vượt trội nguy cơ.</li>\r\n	<li>Trẻ bị dị ứng với một trong c&aacute;c th&agrave;nh phần của vắc xin hay với vắc xin ho g&agrave; (v&ocirc; b&agrave;o hoặc nguy&ecirc;n b&agrave;o), hay trước đ&acirc;y trẻ đ&atilde; c&oacute; phản ứng dị ứng sau khi ti&ecirc;m vắc xin c&oacute; chứa c&aacute;c chất tương tự.</li>\r\n	<li>Trẻ c&oacute; bệnh n&atilde;o tiến triển hoặc tổn thương ở n&atilde;o.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<p>Như tất cả c&aacute;c vắc xin d&ugrave;ng đường ti&ecirc;m kh&aacute;c, phải thận trọng khi sử dụng vắc xin Hexaxim cho người bị giảm tiểu cầu hay bị rối loạn đ&ocirc;ng m&aacute;u v&igrave; c&oacute; thể bị chảy m&aacute;u sau khi ti&ecirc;m bắp.</p>\r\n\r\n<ul>\r\n	<li>Trong vắc xin c&oacute; một lượng rất nhỏ glutaraldehyde, neomycin, streptomycin v&agrave; polymycin B, v&igrave; vậy h&atilde;y cẩn trọng đối với những trẻ c&oacute; tiền sử dị ứng với một trong c&aacute;c th&agrave;nh phần n&agrave;y.</li>\r\n	<li>Cần c&acirc;n nhắc cẩn thận khi quyết định d&ugrave;ng tiếp c&aacute;c liều vắc xin chữa ho g&agrave; nếu đ&atilde; từng c&oacute; bất kỳ một trong c&aacute;c triệu chứng sau đ&acirc;y xảy ra:</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Sốt &ge; 40 độ C trong v&ograve;ng 48 giờ sau khi ti&ecirc;m, m&agrave; kh&ocirc;ng phải do một nguy&ecirc;n nh&acirc;n x&aacute;c định n&agrave;o kh&aacute;c.</li>\r\n	<li>Trụy mạch hay trạng th&aacute;i giống sốc (giảm trương lực- giảm cơ đ&aacute;p ứng) trong v&ograve;ng 48 giờ sau khi ti&ecirc;m ngừa.</li>\r\n	<li>Quấy kh&oacute;c dai dẳng, k&eacute;o d&agrave;i &ge; 3 giờ, xảy ra trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>\r\n	<li>Co giật c&oacute; k&egrave;m theo sốt hay kh&ocirc;ng sốt, xảy ra trong v&ograve;ng 3 ng&agrave;y sau khi ti&ecirc;m.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Nếu trẻ đang/đ&atilde; c&oacute; vấn đề sức khỏe hay bị dị ứng.</li>\r\n	<li>Nếu trước đ&acirc;y, sau khi ti&ecirc;m vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n (vắc xin uốn v&aacute;n)&nbsp; trẻ bị hội hội chứng Guillain-Barr&eacute; (nhạy cảm bất thường, liệt) hay vi&ecirc;m d&acirc;y thần kinh c&aacute;nh tay (liệt, đau lan tỏa ở c&aacute;nh tay v&agrave; vai), b&aacute;c sĩ sẽ quyết định c&oacute; tiếp tục d&ugrave;ng vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n nữa hay kh&ocirc;ng.</li>\r\n	<li>Nếu trẻ đang được điều trị bằng thuốc ức chế miễn dịch hoặc bị suy giảm miễn dịch, th&igrave; đ&aacute;p ứng miễn dịch đối với vắc xin sẽ bị giảm.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<p>Nếu đang d&ugrave;ng thuốc hoặc muốn ti&ecirc;m c&ugrave;ng với c&aacute;c vắc xin kh&aacute;c v&agrave;o c&ugrave;ng một thời điểm, h&atilde;y hỏi &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.</p>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>C&aacute;c phản ứng thường gặp nhất l&agrave; đau chỗ ti&ecirc;m, dễ k&iacute;ch động, quấy kh&oacute;c, tại nơi ti&ecirc;m c&oacute; nổi quầng đỏ. C&aacute;c dấu hiệu v&agrave; triệu chứng n&agrave;y thường gặp trong v&ograve;ng 48 giờ sau khi ti&ecirc;m v&agrave; c&oacute; thể k&eacute;o d&agrave;i 48-72 giờ, thường tự khỏi m&agrave; kh&ocirc;ng cần điều trị đặc hiệu.</li>\r\n	<li>C&aacute;c phản ứng to&agrave;n th&acirc;n: sốt, dễ k&iacute;ch động, buồn ngủ, ch&aacute;n ăn, ti&ecirc;u chảy, &oacute;i mửa, quấy kh&oacute;c k&eacute;o d&agrave;i. Hiếm hơn, c&oacute; thể thấy nổi mề đay, ph&aacute;t ban ngo&agrave;i da, co giật k&egrave;m sốt hoặc kh&ocirc;ng k&egrave;m sốt trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>\r\n	<li>Sau khi ti&ecirc;m c&aacute;c vắc xin chứa th&agrave;nh phần Haemophilus influenzae t&yacute;p b, phản ứng sưng ph&ugrave; chi dưới cũng đ&atilde; được b&aacute;o c&aacute;o. Những phản ứng n&agrave;y khởi ph&aacute;t trong 24-72 giờ sau ti&ecirc;m, đ&ocirc;i khi đi k&egrave;m với sốt, đau v&agrave; quấy kh&oacute;c v&agrave; tự khỏi trong 3-5 ng&agrave;y.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<p>Vắc xin được bảo quản ở nhiệt độ từ 2 độ C đến 8 độ C.<br />\r\n<br />\r\n<span style=\"font-size:14px\"><strong>Đối tượng</strong></span><br />\r\nVắc xin Hexaxim được chỉ định ti&ecirc;m cho trẻ từ 6 tuần tuổi đến 2 tuổi ph&ograve;ng 6 bệnh: Bạch hầu, Ho g&agrave;, Uốn v&aacute;n, Bại liệt, Vi&ecirc;m gan B v&agrave; c&aacute;c bệnh do Hib.</p>\r\n\r\n<p>Lịch ti&ecirc;m 6 trong 1 Hexaxim bao gồm 4 mũi:</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n</li>\r\n	<li>Mũi 2: 1 th&aacute;ng sau mũi 1</li>\r\n	<li>Mũi 3: 1 th&aacute;ng sau mũi 2</li>\r\n	<li>Mũi 4: c&aacute;ch mũi thứ 3 l&agrave; 12 th&aacute;ng (c&aacute;ch tối thiểu 6 th&aacute;ng).</li>\r\n</ul>\r\n\r\n<p>Vắc xin Hexaxim c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, sau khi ti&ecirc;m vắc xin 6 trong 1 Infanrix Hexa (Bỉ), trẻ c&oacute; thể gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt, v&igrave; đ&oacute; l&agrave; đ&aacute;p ứng miễn dịch của cơ thể. Cụ thể như:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: sưng đỏ, đau từ 1 &ndash; 3 ng&agrave;y. C&oacute; thể nổi cục cứng, sau khoảng 1-3 tuần sẽ tự khỏi</li>\r\n	<li>To&agrave;n th&acirc;n: Trẻ c&oacute; thể sốt, quấy kh&oacute;c, n&ocirc;n, ti&ecirc;u chảy, b&uacute; k&eacute;m.</li>\r\n</ul>\r\n\r\n<p>Sau khi ti&ecirc;m c&aacute;c vắc xin chứa th&agrave;nh phần Haemophilus influenzae tu&yacute;p b, c&oacute; thể phản ứng sưng ph&ugrave; chi dưới. Những phản ứng n&agrave;y khởi ph&aacute;t trong 24-72 giờ sau ti&ecirc;m v&agrave; tự khỏi trong 3-5 ng&agrave;y.</p>', 'ncc1', '10', 4, '30', 1, 'images/hinhanhvaccine/20240610_062850.jpg'),
('vcHuyếtthanhuốnván', 'Huyết thanh uốn ván', 'Việt Nam', '175000', 86, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Huyết thanh uốn v&aacute;n SAT được d&ugrave;ng để ph&ograve;ng ngừa uốn v&aacute;n ở người vừa mới bị vết thương c&oacute; thể nhiễm b&agrave;o tử uốn v&aacute;n, bao gồm những người kh&ocirc;ng ti&ecirc;m ngừa uốn v&aacute;n trong 10 năm gần đ&acirc;y, hoặc kh&ocirc;ng nhớ r&otilde; lịch ti&ecirc;m uốn v&aacute;n.&nbsp;</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Huyết thanh uốn v&aacute;n SAT được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi&nbsp;Viện Vắc xin v&agrave; Sinh phẩm Y tế (<a href=\"https://ivac.com.vn/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">IVAC</a>&nbsp;&ndash; Việt Nam).</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Huyết thanh uốn v&aacute;n SAT được chỉ định ti&ecirc;m bắp.</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Những trường hợp c&oacute; tiền sử dị ứng với huyết thanh kh&aacute;ng độc tố uốn v&aacute;n nguồn gốc từ ngựa. Những trường hợp n&agrave;y nếu bắt buộc d&ugrave;ng n&ecirc;n d&ugrave;ng loại huyết thanh uốn v&aacute;n nguồn gốc từ người.</li>\r\n	<li>Phụ nữ đang mang thai.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Cần phải t&igrave;m hiểu tiền sử dị ứng của bệnh nh&acirc;n, sẵn s&agrave;ng thuốc v&agrave; c&aacute;c biện ph&aacute;p chống sốc.</li>\r\n	<li>Thử phản ứng mẫn cảm thuốc trước khi ti&ecirc;m.</li>\r\n	<li>Những trường hợp trước đ&acirc;y chưa d&ugrave;ng huyết thanh từ ngựa, ti&ecirc;m 1 lần hết liều.</li>\r\n	<li>Những trường hợp c&oacute; kết quả phản ứng mẫn cảm dương t&iacute;nh th&igrave; phải d&ugrave;ng phương ph&aacute;p giải mẫn cảm Besredka như sau: ti&ecirc;m bắp hoặc dưới da liều 0,1 ml, theo d&otilde;i 30 ph&uacute;t, nếu kh&ocirc;ng c&oacute; phản ứng, tiếp tục ti&ecirc;m liều 0,25ml theo d&otilde;i 30 ph&uacute;t, nếu kh&ocirc;ng c&oacute; phản ứng xảy ra ti&ecirc;m hết liều c&ograve;n lại.</li>\r\n	<li>Trường hợp cần thiết c&oacute; thể d&ugrave;ng c&aacute;c thuốc kh&aacute;ng histamin trước khi ti&ecirc;m huyết thanh.</li>\r\n	<li>Thời kỳ cho con b&uacute;: hiện tại kh&ocirc;ng c&oacute; dữ liệu về vấn đề n&agrave;y.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Huyết thanh kh&aacute;ng độc tố uốn v&aacute;n kh&ocirc;ng ảnh hưởng tới đ&aacute;p ứng miễn dịch đối với giải độc tố uốn v&aacute;n hoặc giải độc tố uốn v&aacute;n hấp phụ. G&acirc;y miễn dịch chủ động để ph&ograve;ng bệnh uốn v&aacute;n n&ecirc;n tiến h&agrave;nh đồng thời với g&acirc;y miễn dịch thụ động bằng huyết thanh kh&aacute;ng độc tố uốn v&aacute;n; d&ugrave; vậy huyết thanh kh&aacute;ng độc tố uốn v&aacute;n cũng kh&ocirc;ng được trộn lẫn trong c&ugrave;ng một bơm ti&ecirc;m với giải độc tố uốn v&aacute;n hoặc giải độc tố hấp phụ; cũng kh&ocirc;ng được ti&ecirc;m v&agrave;o c&ugrave;ng một vị tr&iacute;, v&igrave; khả năng trung h&ograve;a giải độc tố c&oacute; thể xảy ra.</li>\r\n	<li>Kh&aacute;ng thể huyết thanh kh&aacute;ng độc tố uốn v&aacute;n c&oacute; thể ảnh hưởng tới đ&aacute;p ứng miễn dịch đối với một v&agrave;i loại vắc xin virus sống (vắc xin virus sởi sống, vắc xin virus quai bị sống, vắc xin virus rubella sống), việc d&ugrave;ng c&aacute;c vắc xin n&agrave;y n&ecirc;n ho&atilde;n lại 3 th&aacute;ng sau khi ti&ecirc;m huyết thanh kh&aacute;ng độc tố uốn v&aacute;n.</li>\r\n	<li>Do c&aacute;c chế phẩm c&oacute; chứa globulin miễn dịch kh&ocirc;ng c&oacute; biểu hiện ảnh hưởng tới c&aacute;c đ&aacute;p ứng miễn dịch của vắc xin uống virus bại liệt sống, vắc xin sốt v&agrave;ng hoặc vắc xin thương h&agrave;n đường uống (Ty21a) n&ecirc;n c&aacute;c vắc xin n&agrave;y c&oacute; thể d&ugrave;ng đồng thời hoặc trước hay sau c&ugrave;ng với huyết thanh kh&aacute;ng độc tố uốn v&aacute;n.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Những người c&oacute; cơ địa dị ứng, người d&ugrave;ng huyết thanh nhiều lần thường c&oacute; nguy cơ phản ứng dị ứng với huyết thanh như nổi mề đay, ngứa ph&ugrave;, vi&ecirc;m thận, trường hợp nặng c&oacute; thể bị cho&aacute;ng, sốc phản vệ.</li>\r\n	<li>Biểu hiện dị ứng c&oacute; thể xảy ra ngay tức thời sau khi d&ugrave;ng huyết thanh, sau v&agrave;i giờ hoặc 7 đến 10 ng&agrave;y sau khi ti&ecirc;m.</li>\r\n</ul>\r\n\r\n<h3><strong>Qu&aacute; liều v&agrave; xử l&yacute;</strong></h3>\r\n\r\n<ul>\r\n	<li>Th&ocirc;ng b&aacute;o cho b&aacute;c sĩ c&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn gặp phải khi sử dụng thuốc.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<p>Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8 độ C). Kh&ocirc;ng được đ&oacute;ng băng.</p>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Huyết thanh Uốn v&aacute;n SAT được d&ugrave;ng để dự ph&ograve;ng bệnh uốn v&aacute;n trong trường hợp bị c&aacute;c vết thương, vết cắn s&uacute;c vật. Điều trị bệnh nh&acirc;n bị bệnh uốn v&aacute;n (khi đ&atilde; c&oacute; triệu chứng bệnh).</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p><strong><em>Dự ph&ograve;ng sau khi bị thương:</em></strong></p>\r\n\r\n<p>Nhất thiết phải d&ugrave;ng phương ph&aacute;p Besredka: Ti&ecirc;m 0.1ml, chờ nửa giờ, ti&ecirc;m 0.25ml, chờ nửa giờ, nếu kh&ocirc;ng phản ứng, ti&ecirc;m hết liều c&ograve;n lại. Liều th&ocirc;ng thường huyết thanh kh&aacute;ng độc tố uốn v&aacute;n ở người lớn v&agrave; trẻ em để dự ph&ograve;ng sau khi bị thương l&agrave; 1500 đvqt, ti&ecirc;m c&agrave;ng sớm c&agrave;ng tốt sau khi bị thương. Tăng liều gấp đ&ocirc;i đối với vết thương dễ g&acirc;y uốn v&aacute;n hoặc chậm trễ khi bắt đầu ti&ecirc;m ph&ograve;ng hoặc ở người c&oacute; thể trọng qu&aacute; cao.</p>\r\n\r\n<p><strong><em>Điều trị uốn v&aacute;n:</em></strong></p>\r\n\r\n<p>Mặc d&ugrave; liều điều trị tối ưu v&agrave; liều c&oacute; hiệu quả trong điều trị bệnh uốn v&aacute;n c&ograve;n chưa được x&aacute;c định, liều khuy&ecirc;n d&ugrave;ng cho người lớn v&agrave; trẻ em l&agrave; 3000 &ndash; 6000 đơn vị.</p>\r\n\r\n<p>Uốn v&aacute;n sơ sinh: 5000 &ndash; 10.000 đvqt.</p>\r\n\r\n<p>Trẻ em v&agrave; người lớn: 50.000 &ndash; 100.000 đvqt, ti&ecirc;m dưới da 1/2 liều v&agrave; nửa c&ograve;n lại ti&ecirc;m bắp.</p>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<p>Với bệnh uốn v&aacute;n, lu&ocirc;n khuyến c&aacute;o người ti&ecirc;m đi ti&ecirc;m c&agrave;ng sớm c&agrave;ng tốt kh&ocirc;ng cần điều kiện g&igrave; đặc biệt. Kh&aacute;ch h&agrave;ng n&ecirc;n sắp xếp đến&nbsp;<a href=\"https://vnvc.vn/\" rel=\"noopener\" target=\"_blank\">Trung t&acirc;m VNVC</a>&nbsp;để b&aacute;c sĩ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp.</p>\r\n\r\n<h2>5. Khoảng c&aacute;ch với c&aacute;c vắc xin kh&aacute;c</h2>\r\n\r\n<p>Kh&aacute;ng thể huyết thanh kh&aacute;ng độc tố uốn v&aacute;n c&oacute; thể ảnh hưởng tới đ&aacute;p ứng miễn dịch đối với một v&agrave;i loại vắc xin virus sống (vắc xin virus sởi sống, vắc xin virus quai bị sống, vắc xin virus rubella sống), việc d&ugrave;ng c&aacute;c vắc xin n&agrave;y n&ecirc;n ho&atilde;n lại 3 th&aacute;ng sau khi ti&ecirc;m huyết thanh kh&aacute;ng độc tố uốn v&aacute;n.</p>\r\n\r\n<h2>6. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Huyết thanh Uốn v&aacute;n SAT c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, những người c&oacute; cơ địa dị ứng, người d&ugrave;ng huyết thanh nhiều lần thường c&oacute; nguy cơ phản ứng dị ứng với huyết thanh như nổi mề đay, ngứa ph&ugrave;, vi&ecirc;m thận, trường hợp nặng c&oacute; thể bị cho&aacute;ng, sốc phản vệ.</p>\r\n\r\n<p>Biểu hiện dị ứng c&oacute; thể xảy ra ngay tức thời sau khi d&ugrave;ng huyết thanh, sau v&agrave;i giờ hoặc 7 đến 10 ng&agrave;y sau khi ti&ecirc;m.</p>', 'ncc2', '21', 1, '300', 1, 'images/hinhanhvaccine/20240626_163236.jpg'),
('vcInfranrixIPV+Hib', 'Infranrix IPV + Hib', 'Bỉ', '1015000', 35, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin Infanrix IPV+Hib l&agrave; vắc xin kết hợp ph&ograve;ng được 5 loại bệnh trong 1 mũi ti&ecirc;m, bao gồm: Ho g&agrave;, bạch hầu, uốn v&aacute;n, bại liệt v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o mủ do H. Influenzae t&yacute;p B (Hib). T&iacute;ch hợp trong một loại vắc xin,&nbsp;Infanrix IPV+Hib&nbsp;gi&uacute;p giảm số mũi ti&ecirc;m, đồng nghĩa với việc hạn chế đau đớn cho b&eacute; khi phải ti&ecirc;m qu&aacute; nhiều.&nbsp;</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Infanrix IPV+Hib được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học Glaxosmithkline (<a href=\"https://en.wikipedia.org/wiki/GlaxoSmithKline\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">GSK</a>) &ndash; Bỉ.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Vắc xin Infanrix IPV+Hib được chỉ định ti&ecirc;m bắp s&acirc;u (ở mặt trước-b&ecirc;n đ&ugrave;i).&nbsp;</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Trẻ qu&aacute; mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin Infanrix IPV+Hib.</li>\r\n	<li>Trẻ đ&atilde; từng c&oacute; phản ứng qu&aacute; mẫn sau mũi ti&ecirc;m vắc xin bạch hầu, ho g&agrave;, uốn v&aacute;n, bại liệt bất hoạt hoặc Hib trước đ&oacute;.</li>\r\n	<li>Trẻ mắc c&aacute;c bệnh về n&atilde;o nhưng kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, xảy ra trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m vắc xin ho g&agrave; trước đ&oacute;.</li>\r\n	<li>Kh&ocirc;ng khuyến c&aacute;o sử dụng cho người lớn, trẻ vị th&agrave;nh ni&ecirc;n v&agrave; trẻ em từ 5 tuổi trở l&ecirc;n.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Ho&atilde;n ti&ecirc;m vắc xin cho c&aacute;c đối tượng đang sốt.</li>\r\n	<li>Rối loạn thần kinh tiến triển bao gồm chứng co thắt, động kinh kh&ocirc;ng kiểm so&aacute;t hoặc bệnh n&atilde;o tiến triển (ho&atilde;n ti&ecirc;m cho đến khi bệnh khỏi hoặc ổn định, cần c&acirc;n nhắc yếu tố nguy cơ/ lợi &iacute;ch).</li>\r\n	<li>Thận trọng khi ti&ecirc;m vắc xin cho những đối tượng bị giảm tiểu cầu hoặc rối loạn m&aacute;u chảy m&aacute;u v&igrave; c&oacute; thể g&acirc;y chảy m&aacute;u sau khi ti&ecirc;m bắp ở những đối tượng n&agrave;y.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>C&oacute; thể ti&ecirc;m vắc xin Infanrix IPV + Hib đồng thời với vắc xin: vi&ecirc;m gan B, phế cầu khuẩn, Rotavirus v&agrave; một số loại vắc xin kh&aacute;c.</li>\r\n	<li>N&ecirc;n ti&ecirc;m vắc xin Infanrix IPV + Hib đ&atilde; ho&agrave;n nguy&ecirc;n v&agrave; vắc xin ti&ecirc;m kh&aacute;c ở những vị tr&iacute; kh&aacute;c nhau.</li>\r\n	<li>Cũng như vắc xin kh&aacute;c, vắc xin Infanrix IPV + Hib c&oacute; thể kh&ocirc;ng đạt được đ&aacute;p ứng miễn dịch như mong đợi ở những bệnh nh&acirc;n bị suy giảm miễn dịch hoặc đang điều trị ức chế miễn dịch.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>C&aacute;c phản ứng thường gặp: đau, đỏ, sưng tại vị tr&iacute; ti&ecirc;m. C&aacute;c dấu hiệu v&agrave; triệu chứng n&agrave;y thường gặp trong v&ograve;ng 48 giờ sau khi ti&ecirc;m, thường tự khỏi m&agrave; kh&ocirc;ng cần điều trị đặc hiệu.</li>\r\n	<li>C&aacute;c phản ứng to&agrave;n th&acirc;n: c&oacute; thể sốt, dễ k&iacute;ch th&iacute;ch, buồn ngủ, rối loạn giấc ngủ v&agrave; ăn uống, ti&ecirc;u chảy, n&ocirc;n, quấy kh&oacute;c, bứt rứt.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin Infanrix IPV+Hib được bảo quản ở nhiệt độ 2-8 độ C. Kh&ocirc;ng được để th&agrave;nh phần Infanrix IPV đ&ocirc;ng băng. Tr&aacute;nh &aacute;nh s&aacute;ng.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Infanrix IPV+Hib được chỉ định ti&ecirc;m cho trẻ từ 2 th&aacute;ng tuổi&nbsp;đến trước sinh nhật 5 tuổi ph&ograve;ng 5 bệnh: bạch hầu, ho g&agrave;, uốn v&aacute;n, bại liệt v&agrave; c&aacute;c bệnh do Hib.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p>Lịch ti&ecirc;m vắc xin 5 trong 1 Infanrix IPV+Hib gồm 5 mũi:</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: Lần đầu ti&ecirc;n.</li>\r\n	<li>Mũi 2: 1 th&aacute;ng sau mũi 1.</li>\r\n	<li>Mũi 3: 1 th&aacute;ng sau mũi 2.</li>\r\n	<li>Mũi 4: 1 năm sau mũi 3 (tối thiểu 6 th&aacute;ng sau mũi 3).</li>\r\n	<li>Mũi nhắc: 3 năm sau mũi 4.</li>\r\n</ul>\r\n\r\n<h2>4. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Kh&ocirc;ng cần điều kiện đặc biệt n&agrave;o trước ti&ecirc;m vắc xin 5 trong 1 Infanrix IPV+Hib. Qu&yacute; kh&aacute;ch c&oacute; thể sắp xếp đến&nbsp;<a href=\"https://vnvc.vn/\" rel=\"noopener\" target=\"_blank\">Trung t&acirc;m VNVC</a>&nbsp;để b&aacute;c sĩ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Vắc xin Infanrix IPV+Hib c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, người được ti&ecirc;m c&oacute; thể gặp một số phản ứng nhẹ, k&eacute;o d&agrave;i từ 1- 2 ng&agrave;y:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đau, đỏ, sưng thường diễn ra từ 1-4 ng&agrave;y sau ti&ecirc;m.</li>\r\n	<li>To&agrave;n th&acirc;n: sốt, k&iacute;ch th&iacute;ch, ch&aacute;n ăn.</li>\r\n</ul>', 'ncc4', '10', 3, '30', 1, 'images/hinhanhvaccine/20240626_164450.jpg');
INSERT INTO `vaccine` (`mavc`, `tenvc`, `nuocsx`, `gia`, `soluong`, `thongtinvc`, `mancc`, `benh_nhombenh`, `solieu`, `khoangcachmuitiem`, `trangthaivc`, `hinhanhvc`) VALUES
('vcMenactra', 'Menactra', 'Hoa Kỳ', '495000', 58, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin Menactra được chỉ định để tạo miễn dịch chủ động cơ bản v&agrave; nhắc lại ph&ograve;ng bệnh x&acirc;m lấn do N.meningitidis (vi khuẩn n&atilde;o m&ocirc; cầu) c&aacute;c nh&oacute;m huyết thanh A, C, Y, W-135 g&acirc;y ra, như: vi&ecirc;m m&agrave;ng n&atilde;o, nhiễm tr&ugrave;ng huyết, vi&ecirc;m phổi&hellip;</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Menactra được sản xuất bởi h&atilde;ng vắc xin h&agrave;ng đầu thế giới &ndash;&nbsp;<a href=\"https://www.sanofi.com/en/your-health/vaccines\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Sanofi Pasteur</a>&nbsp;(Ph&aacute;p). Được sản xuất tại Mỹ.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Menactra được chỉ định ti&ecirc;m bắp, tốt nhất l&agrave; ở mặt trước &ndash; ngo&agrave;i của đ&ugrave;i hoặc v&ugrave;ng cơ delta t&ugrave;y theo tuổi v&agrave; khối cơ của đối tượng. Kh&ocirc;ng được ti&ecirc;m tĩnh mạch hoặc ti&ecirc;m trong da &amp; dưới da đối với vắc xin Menactra.</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Người đ&atilde; bị phản ứng qu&aacute; mẫn to&agrave;n th&acirc;n với bất cứ th&agrave;nh phần của vắc xin, hoặc sau một lần ti&ecirc;m vắc xin n&agrave;y hoặc một vắc xin chứa c&ugrave;ng một th&agrave;nh phần trước đ&acirc;y.</li>\r\n	<li>Sốt hay bệnh cấp t&iacute;nh: th&ocirc;ng thường, trong trường hợp sốt vừa hoặc nặng v&agrave;/hoặc bệnh cấp t&iacute;nh n&ecirc;n tr&igrave; ho&atilde;n ti&ecirc;m chủng.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Rất thận trọng khi ti&ecirc;m vắc xin vi&ecirc;m m&agrave;ng n&atilde;o m&ocirc; cầu Menactra cho phụ nữ c&oacute; thai, cần c&acirc;n nhắc giữa lợi &iacute;ch v&agrave; nguy cơ hoặc trong trường hợp thật cần thiết.</li>\r\n	<li>Lu&ocirc;n c&oacute; sẵn thiết bị v&agrave; ph&aacute;c đồ ph&ograve;ng sốc phản vệ.</li>\r\n	<li>Bệnh nh&acirc;n suy giảm miễn dịch, thiếu hụt bổ thể hoặc đang d&ugrave;ng c&aacute;c thuốc ức chế miễn dịch c&oacute; thể kh&ocirc;ng đ&aacute;p miễn dịch đầy đủ với vắc xin.</li>\r\n	<li>Người được chẩn đo&aacute;n mắc hội chứng Guillain-Barr&eacute; (GBS) trước đ&acirc;y c&oacute; thể tăng nguy cơ bị GBS sau khi ti&ecirc;m Menactra. Cần lưu &yacute; lợi &iacute;ch v&agrave; nguy cơ tiềm t&agrave;ng khi quyết định sử dụng vắc xin.</li>\r\n	<li>Ngất (bất tỉnh) đ&atilde; được b&aacute;o c&aacute;o sau khi ti&ecirc;m chủng với Menactra. Cần c&oacute; sẵn c&aacute;c quy tr&igrave;nh đề ph&ograve;ng thương t&iacute;ch do t&eacute; ng&atilde; v&agrave; xử tr&iacute; phản ứng ngất.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<p>C&aacute;c phản ứng bất lợi to&agrave;n th&acirc;n v&agrave; tại chỗ c&oacute; thể gặp sau khi ti&ecirc;m Menactra được ghi nhận qua 1 số nghi&ecirc;n cứu thử nghiệm l&acirc;m s&agrave;ng cho thấy:</p>\r\n\r\n<ul>\r\n	<li>Trẻ từ 9 th&aacute;ng đến 12 th&aacute;ng tuổi: Nhạy chỗ ti&ecirc;m (đau, nổi ban đỏ hoặc sưng nhẹ &ndash; những triệu chứng n&agrave;y thường biến mất sau 72 giờ) v&agrave; c&aacute;u kỉnh.</li>\r\n	<li>Trẻ từ 2 đến 10 tuổi: Đau tại vị tr&iacute; ti&ecirc;m v&agrave; c&aacute;u kỉnh. Ti&ecirc;u chảy, ngủ g&agrave; v&agrave; ch&aacute;n ăn cũng thường xảy ra.</li>\r\n	<li>Thanh thiếu ni&ecirc;n từ 11 đến 18 tuổi &amp; c&aacute;c đối tượng trưởng th&agrave;nh từ 18 đến 55 tuổi: Đau tại vị tr&iacute; ti&ecirc;m, đau đầu v&agrave; mệt mỏi.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<p>Vắc xin được bảo quản ở nhiệt độ từ 2 độ C đến 8 độ C. Kh&ocirc;ng được đ&ocirc;ng băng.&nbsp;</p>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Menactra (Mỹ) được chỉ định ti&ecirc;m cho trẻ từ 9 th&aacute;ng đến 55 tuổi (trước sinh nhật lần thứ 56) ph&ograve;ng bệnh&nbsp;<a href=\"https://vnvc.vn/viem-mang-nao-mo-cau-acyw/\" rel=\"noopener\" target=\"_blank\">vi&ecirc;m m&agrave;ng n&atilde;o m&ocirc; cầu ACYW</a>&nbsp;do c&aacute;c nh&oacute;m huyết thanh A,C,Y, W-135 g&acirc;y ra.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p><strong>Người sức khỏe b&igrave;nh thường:</strong></p>\r\n\r\n<p><em>Trẻ từ 9 th&aacute;ng đến dưới 24 th&aacute;ng: 2 liều c&aacute;ch nhau &iacute;t nhất 3 th&aacute;ng</em></p>\r\n\r\n<ul>\r\n	<li>2 mũi cơ bản c&aacute;ch nhau &iacute;t nhất 3 th&aacute;ng</li>\r\n	<li>Mũi ti&ecirc;m nhắc: cho Kh&aacute;ch h&agrave;ng từ 15 đến &lt; 56 tuổi</li>\r\n</ul>\r\n\r\n<p><em>Trẻ em từ 2 tuổi đến 55 tuổi (trước sinh nhật lần thứ 56):&nbsp;</em></p>\r\n\r\n<ul>\r\n	<li>Mũi 1: lần đầu ti&ecirc;n</li>\r\n	<li>Mũi 2 (mũi nhắc): c&aacute;ch mũi 1 &iacute;t nhất 4 năm (cho người từ 15 đến 55 tuổi)</li>\r\n</ul>\r\n\r\n<p><strong>Người c&oacute; nguy cơ cao mắc N&atilde;o m&ocirc; cầu (c&oacute; bệnh l&yacute; nền, d&ugrave;ng thuốc ức chế miễn dịch&hellip;)*:</strong></p>\r\n\r\n<p><em>Trẻ dưới 7 tuổi:</em></p>\r\n\r\n<ul>\r\n	<li>2 mũi cơ bản c&aacute;ch nhau &iacute;t nhất 2 th&aacute;ng</li>\r\n	<li>Ti&ecirc;m 1 mũi nhắc sau mũi cơ bản cuối c&ugrave;ng &iacute;t nhất 3 năm</li>\r\n	<li>Sau đ&oacute; mỗi 5 năm</li>\r\n</ul>\r\n\r\n<p><em>Người tr&ograve;n 7 tuổi trở l&ecirc;n v&agrave; người lớn:</em></p>\r\n\r\n<ul>\r\n	<li>2 mũi cơ bản c&aacute;ch nhau &iacute;t nhất 2 th&aacute;ng</li>\r\n	<li>Sau đ&oacute; mỗi 5 năm</li>\r\n</ul>\r\n\r\n<p>* Theo khuyến c&aacute;o của CDC (Hoa Kỳ).</p>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<p><strong>Khi ti&ecirc;m vắc xin Menactra cần lưu &yacute; g&igrave; kh&ocirc;ng?</strong></p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch lưu &yacute; một số khoảng c&aacute;ch giữa vắc xin Menactra với c&aacute;c vắc xin sau:</p>\r\n\r\n<p><strong>Vắc xin Menactra v&agrave; vắc xin chứa th&agrave;nh phần Bạch hầu, ho g&agrave;, uốn v&aacute;n l&agrave; 6in1, 5in1, 4in1, 3in1 (DPT của Ti&ecirc;m chủng mở rộng) c&aacute;ch nhau thế n&agrave;o?</strong></p>\r\n\r\n<p>Vắc xin chứa th&agrave;nh phần Bạch hầu, ho g&agrave;, uốn v&aacute;n (6 in 1 hoặc 5 in 1 hoặc 4 in 1 hoặc DPT):&nbsp;Đối với người cắt l&aacute;ch/mất chức năng l&aacute;ch/suy giảm miễn dịch/thiếu hụt bổ thể:&nbsp;</p>\r\n\r\n<p>o&nbsp;&nbsp;Ti&ecirc;m Menactra trước hoặc đồng thời với vắc xin 6 in 1 hoặc 5 in 1 hoặc 4 in 1 hoặc DPT.&nbsp;</p>\r\n\r\n<p>o&nbsp;&nbsp;Nếu Menactra ti&ecirc;m sau vắc xin 6 in 1 hoặc 5 in 1 hoặc 4 in 1 hoặc DPT: phải tu&acirc;n thủ khoảng c&aacute;ch theo khuyến c&aacute;o của b&aacute;c sĩ để đảm bảo hiệu quả của vắc xin được tốt nhất.&nbsp;</p>\r\n\r\n<p><strong>Khoảng c&aacute;ch giữa vắc xin Menactra v&agrave; vắc xin Prevenar 13 l&agrave; bao l&acirc;u?</strong></p>\r\n\r\n<p>Đối với người suy giảm chức năng l&aacute;ch, cắt l&aacute;ch/suy giảm miễn dịch/thiếu hụt bổ thể:</p>\r\n\r\n<p>o&nbsp;&nbsp;Kh&ocirc;ng ti&ecirc;m vắc xin Menactra v&agrave; Prevenar 13 trong c&ugrave;ng buổi ti&ecirc;m chủng.</p>\r\n\r\n<p>o&nbsp;&nbsp;N&ecirc;n ho&agrave;n tất lịch ti&ecirc;m Prevenar 13 trước, trường hợp bất khả kh&aacute;ng c&oacute; thể ti&ecirc;m xen kẽ nhưng phải đảm bảo khoảng c&aacute;ch giữa Prevenar 13 v&agrave; Menactra theo khuyến c&aacute;o của b&aacute;c sĩ để đạt được hiệu quả tối ưu.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Sau khi ti&ecirc;m vắc xin ph&ograve;ng bệnh Menactra, trẻ thường gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể. Cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đau, sưng, đỏ tại vị tr&iacute; ti&ecirc;m</li>\r\n	<li>To&agrave;n th&acirc;n: ngủ g&agrave;, c&aacute;u kỉnh, nhức đầu, mệt mỏi, ti&ecirc;u chảy, ch&aacute;n ăn. C&aacute;c triệu chứng thường nhẹ v&agrave; tho&aacute;ng qua.</li>\r\n</ul>\r\n\r\n<p>C&aacute;c phản ứng sau ti&ecirc;m c&oacute; thể gặp trong v&ograve;ng 7 ng&agrave;y sau ti&ecirc;m chủng</p>', 'ncc2', '24', 2, '60', 1, 'images/hinhanhvaccine/20240626_164826.jpg'),
('vcMengocBC', 'Mengoc BC', 'Ba Lan', '1000000', 307, '<p>Vắc xin VA-Mengoc-BC ph&ograve;ng bệnh vi&ecirc;m m&agrave;ng n&atilde;o do n&atilde;o m&ocirc; cầu khuẩn&nbsp;Meningococcal&nbsp;tu&yacute;p B v&agrave; C g&acirc;y ra.</p>\r\n\r\n<h2><strong>1. TH&Ocirc;NG TIN</strong></h2>\r\n\r\n<h4><strong>Nguồn gốc</strong></h4>\r\n\r\n<p>Vắc xin VA-Mengoc-BC được nghi&ecirc;n cứu v&agrave; sản xuất bởi&nbsp;<a href=\"https://www.finlay.edu.cu/en/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Finlay Institute</a>&nbsp;(Cu Ba).</p>\r\n\r\n<h4><strong>Đường ti&ecirc;m</strong></h4>\r\n\r\n<ul>\r\n	<li>Ti&ecirc;m bắp s&acirc;u, tốt nhất l&agrave; v&agrave;o v&ugrave;ng cơ delta c&aacute;nh tay. Tuy nhi&ecirc;n ở trẻ nhỏ c&oacute; thể ti&ecirc;m bắp đ&ugrave;i, ở mặt trước ngo&agrave;i của đ&ugrave;i.</li>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m tĩnh mạch.</li>\r\n</ul>\r\n\r\n<h4><strong>Chống chỉ định</strong></h4>\r\n\r\n<ul>\r\n	<li>Người qu&aacute; mẫn với c&aacute;c th&agrave;nh phần của vắc xin.</li>\r\n	<li>Người đang sốt, nhiễm khuẩn cấp t&iacute;nh, dị ứng đang tiến triển.</li>\r\n	<li>Hiếm khi c&oacute; phản ứng dị ứng nhưng cần ngưng liều thứ 2 nếu liều 1 c&oacute; dấu hiệu dị ứng.</li>\r\n</ul>\r\n\r\n<h4><strong>Thận trọng khi sử dụng</strong></h4>\r\n\r\n<ul>\r\n	<li>Rất thận trọng khi ti&ecirc;m vắc xin vi&ecirc;m m&agrave;ng n&atilde;o m&ocirc; cầu BC cho phụ nữ c&oacute; thai, cần c&acirc;n nhắc giữa lợi &iacute;ch v&agrave; nguy cơ hoặc trong trường hợp thật cần thiết.</li>\r\n	<li>Lu&ocirc;n c&oacute; sẵn thiết bị v&agrave; ph&aacute;c đồ ph&ograve;ng sốc phản vệ.</li>\r\n	<li>Bệnh nh&acirc;n suy giảm miễn dịch hoặc đang d&ugrave;ng c&aacute;c thuốc ức chế miễn dịch c&oacute; thể kh&ocirc;ng đ&aacute;p miễn dịch đầy đủ với vắc xin.</li>\r\n</ul>\r\n\r\n<h4><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h4>\r\n\r\n<ul>\r\n	<li>&Iacute;t gặp: tại vết ti&ecirc;m c&oacute; đau, nổi ban đỏ hoặc sưng nhẹ. Những triệu chứng n&agrave;y thường biến mất sau 72 giờ.</li>\r\n	<li>To&agrave;n th&acirc;n: c&oacute; thể c&oacute; sốt nhẹ, hoặc cảm gi&aacute;c kh&oacute; chịu, đau đầu, buồn ngủ.</li>\r\n</ul>\r\n\r\n<h4><strong>Bảo quản</strong></h4>\r\n\r\n<p>Vắc xin cần được bảo quản ở nhiệt độ từ 2 độ C đến 8 độ C v&agrave; kh&ocirc;ng được đ&ocirc;ng băng.</p>\r\n\r\n<h2><strong>2. Đối tượng</strong></h2>\r\n\r\n<p>Vắc xin ph&ograve;ng vi&ecirc;m m&agrave;ng n&atilde;o do m&ocirc; cầu BC của CuBa được chỉ định ti&ecirc;m cho trẻ từ 6 tháng tu&ocirc;̉i trở l&ecirc;n và người lớn đ&ecirc;́n 45 tu&ocirc;̉i.</p>\r\n\r\n<h2><strong>3. Ph&aacute;c đồ, lịch ti&ecirc;m</strong></h2>\r\n\r\n<p>Vắc xin VA-Mengoc-BC c&oacute; lịch ti&ecirc;m 2 mũi c&aacute;ch nhau 45 ng&agrave;y.</p>', 'ncc1', '22', 2, '45', 1, 'images/hinhanhvaccine/20240614_055146.jpg'),
('vcMvvac', 'Mvvac', 'Việt Nam', '396000', 45, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin MVVAC tạo miễn dịch chủ động ph&ograve;ng bệnh sởi cho trẻ từ 9 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người chưa c&oacute; kh&aacute;ng thể sởi.</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin MVVAC được nghi&ecirc;n cứu v&agrave; sản xuất bởi&nbsp;<a href=\"http://polyvac.com.vn/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Polyvac</a>&nbsp;&ndash; Việt Nam.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Vắc xin&nbsp;MVVAC&nbsp;chỉ được ti&ecirc;m dưới da, kh&ocirc;ng được ti&ecirc;m tĩnh mạch.</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Trường hợp mẫn cảm với bất cứ th&agrave;nh phần n&agrave;o của vắc xin;</li>\r\n	<li>Phụ nữ c&oacute; thai;</li>\r\n	<li>Trường hợp bị nhiễm tr&ugrave;ng cấp t&iacute;nh đường h&ocirc; hấp, mắc bệnh lao tiến triển chưa được điều trị hay</li>\r\n	<li>Người bị suy giảm miễn dịch (trừ trẻ em bị HIV);</li>\r\n	<li>Người bị bệnh &aacute;c t&iacute;nh.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Cần thận trọng đối với những trường hợp c&oacute; tiền sử sốt cao, dị ứng, co giật, tổn thương n&atilde;o, giảm tiểu cầu;</li>\r\n	<li>Kh&ocirc;ng n&ecirc;n ti&ecirc;m vắc xin trong trường hợp đang sốt hay đang trong qu&aacute; tr&igrave;nh điều trị c&oacute; ảnh hưởng đến hệ miễn dịch (như d&ugrave;ng thuốc, truyền m&agrave;u, hay c&aacute;c chế phẩm từ m&aacute;u&hellip;). Chỉ ti&ecirc;m vắc xin cho trẻ khi đ&atilde; hết sốt &iacute;t nhất l&agrave; 3 ng&agrave;y v&agrave; sau khi kết th&uacute;c điều trị ảnh hưởng đến hệ miễn dịch tối thiểu l&agrave; 4 tuần.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng thấy xuất hiện phản ứng phụ nghi&ecirc;m trọng sau khi ti&ecirc;m vắc xin sởi MVVAC.</li>\r\n	<li>Những phản ứng phụ c&oacute; thể gặp l&agrave; đau, sưng v&agrave; ban đỏ tại chỗ ti&ecirc;m. Phản ứng n&agrave;y thường l&agrave; nhẹ v&agrave; sẽ hết sau khi ti&ecirc;m 1-2 ng&agrave;y.</li>\r\n	<li>Những phản ứng to&agrave;n th&acirc;n như sốt, ban, ho v&agrave; sổ mũi cũng c&oacute; thể xảy ra ở một v&agrave;i trẻ em v&agrave; thường k&eacute;o d&agrave;i từ 1 đến 3 ng&agrave;y rồi tự khỏi m&agrave; kh&ocirc;ng cần điều trị.</li>\r\n	<li>C&oacute; xuất hiện trường hợp ti&ecirc;u chảy nhẹ nhưng chưa đủ bằng chứng kết luận nguy&ecirc;n nh&acirc;n l&agrave; do vắc xin.</li>\r\n	<li>Th&ocirc;ng b&aacute;o cho b&aacute;c sĩ c&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn khi sử dụng vắc xin.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Lọ vắc xin sởi dạng đ&ocirc;ng kh&ocirc; được bảo quản ở khoảng nhiệt độ&nbsp; &le; 8 độ C v&agrave; tr&aacute;nh &aacute;nh s&aacute;ng.</li>\r\n	<li>Lọ nước pha ti&ecirc;m được bảo quản nhiệt độ dưới 30oC, kh&ocirc;ng được l&agrave;m đ&ocirc;ng băng.</li>\r\n	<li>Lọ vắc xin sau khi pha hồi chỉnh bằng nước pha ti&ecirc;m sẽ được bảo quản ở nhiệt độ 2 độ C đến 8 độ C v&agrave; chỉ sử dụng trong v&ograve;ng 6 giờ.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p><strong>Vắc xin MVVAC ph&ograve;ng bệnh sởi cho trẻ từ 9 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người chưa c&oacute; kh&aacute;ng thể sởi.</strong></p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p>Lịch ti&ecirc;m MVVAC gồm 3 mũi như sau:</p>\r\n\r\n<p>&ndash; Mũi 1: mũi đầu ti&ecirc;n khi trẻ đến ti&ecirc;m (9 &ndash; &lt;12 th&aacute;ng tuổi).</p>\r\n\r\n<p>&ndash; Mũi 2: MMR 1: &iacute;t nhất 3 th&aacute;ng sau mũi sởi đơn MVVAC.</p>\r\n\r\n<p>&ndash; Mũi 3: MMR 2: &iacute;t nhất 3 năm sau mũi MMR 1.</p>\r\n\r\n<p><a href=\"https://vnvc.vn/\" rel=\"noopener\" target=\"_blank\">VNVC</a>&nbsp;đang c&oacute; vắc xin ph&ograve;ng bệnh Sởi đơn l&agrave; MVVAC (Việt Nam) cho trẻ từ 9 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người lớn c&oacute; 2 h&igrave;nh thức lựa chọn như sau:</p>\r\n\r\n<ol>\r\n	<li>Ti&ecirc;m đơn liều, kh&aacute;ch h&agrave;ng sẽ mua trọn lọ vắc xin Sởi đơn c&oacute; gi&aacute; mua lẻ v&agrave; ti&ecirc;m ngay l&agrave; 339.000 đồng/1 lọ.</li>\r\n	<li>Ti&ecirc;m đa liều, kh&aacute;ch h&agrave;ng sẽ mua theo liều ri&ecirc;ng lẻ gi&aacute; mua lẻ v&agrave; ti&ecirc;m ngay l&agrave; 225.000 đồng/1 lần ti&ecirc;m.</li>\r\n</ol>\r\n\r\n<p>Ti&ecirc;m đơn liều: 1 lọ/ 1 kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Ti&ecirc;m đa liều: 1 lọ/ 5 kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Ti&ecirc;m đa liều chỉ &aacute;p dụng khi đến ti&ecirc;m trong buổi s&aacute;ng, kh&aacute;ch h&agrave;ng đi ti&ecirc;m buổi s&aacute;ng. Trường hợp kh&aacute;ch h&agrave;ng vẫn muốn ti&ecirc;m v&agrave;o buổi chiều, VNVC mời kh&aacute;ch h&agrave;ng đến ti&ecirc;m theo giờ thuận tiện của kh&aacute;ch h&agrave;ng (VNVC l&agrave;m việc từ 7h30-17 giờ h&agrave;ng ng&agrave;y). Nếu tại thời điểm đ&oacute;, VNVC c&oacute; sẵn vắc xin đa liều th&igrave; kh&aacute;ch h&agrave;ng c&oacute; thể chọn mua. Nếu VNVC kh&ocirc;ng c&oacute; sẵn vắc xin đa liều, Kh&aacute;ch h&agrave;ng vui l&ograve;ng chọn vắc xin đơn liều.</p>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<ul>\r\n	<li><strong><em>Ti&ecirc;m vắc xin MVVAC cần lưu &yacute; điều g&igrave;?</em></strong></li>\r\n</ul>\r\n\r\n<p>Người được ti&ecirc;m cần lưu &yacute; trong v&ograve;ng 1 th&aacute;ng gần nhất kh&ocirc;ng ti&ecirc;m c&aacute;c vắc xin sống giảm độc lực.</p>\r\n\r\n<h2>5. Khoảng c&aacute;ch với c&aacute;c vắc xin kh&aacute;c</h2>\r\n\r\n<ul>\r\n	<li><strong><em>Vắc xin Sởi MVVAC v&agrave; vắc xin kh&aacute;c c&aacute;ch nhau thế n&agrave;o?</em></strong></li>\r\n</ul>\r\n\r\n<p>Th&ocirc;ng thường, c&aacute;c b&aacute;c sĩ khuyến c&aacute;o ti&ecirc;m vắc xin n&agrave;y c&aacute;ch nhau khoảng 2 tuần với c&aacute;c vắc xin kh&aacute;c, trừ c&aacute;c vắc xin sống giảm độc lực th&igrave; cần ti&ecirc;m c&aacute;ch vắc xin sởi &iacute;t nhất l&agrave; 1 th&aacute;ng. Trước khi ti&ecirc;m c&aacute;c b&aacute;c sĩ sẽ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<h2>6. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<ul>\r\n	<li><strong><em>Sau khi ti&ecirc;m sởi, người ti&ecirc;m c&oacute; sốt kh&ocirc;ng?</em></strong></li>\r\n</ul>\r\n\r\n<p>Sau khi ti&ecirc;m vắc xin ph&ograve;ng bệnh sởi, trẻ hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể. Cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đau, đỏ, sưng</li>\r\n	<li>To&agrave;n th&acirc;n: Sốt, ban, ho v&agrave; sổ mũi</li>\r\n</ul>\r\n\r\n<p>C&aacute;c triệu chứng n&agrave;y thường k&eacute;o d&agrave;i từ 1 đến 3 ng&agrave;y rồi tự khỏi m&agrave; kh&ocirc;ng cần điều trị.</p>', 'ncc2', '14', 3, '180', 1, 'images/hinhanhvaccine/20240626_162233.jpg'),
('vcPentaxim', 'Pentaxim', 'Pháp', '795000', 123, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin Pentaxim l&agrave; vắc xin kết hợp ph&ograve;ng được 5 loại bệnh trong 1 mũi ti&ecirc;m, bao gồm: Ho g&agrave;, bạch hầu, uốn v&aacute;n, bại liệt v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o mủ do H.Influenzae t&yacute;p B (Hib). T&iacute;ch hợp trong duy nhất trong 1 vắc xin, 5 trong 1 Pentaxim gi&uacute;p giảm số mũi ti&ecirc;m, đồng nghĩa với việc hạn chế đau đớn cho b&eacute; khi phải ti&ecirc;m qu&aacute; nhiều.</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Pentaxim được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học&nbsp;<a href=\"https://www.sanofi.com/en\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Sanofi Pasteur</a>&nbsp;(Ph&aacute;p).</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Vắc xin Pentaxim được chỉ định ti&ecirc;m bắp (ở mặt trước &ndash; b&ecirc;n đ&ugrave;i).</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Chống chỉ định đối với những trường hợp mẫn cảm với c&aacute;c hoạt chất hay bất cứ t&aacute; dược n&agrave;o trong thuốc.</li>\r\n	<li>Trẻ bị dị ứng với một trong c&aacute;c th&agrave;nh phần của vắc xin hay với vắc xin ho g&agrave; (v&ocirc; b&agrave;o hoặc nguy&ecirc;n b&agrave;o), hay trước đ&acirc;y trẻ đ&atilde; c&oacute; phản ứng dị ứng sau khi ti&ecirc;m vắc xin c&oacute; chứa c&aacute;c chất tương tự.</li>\r\n	<li>Trẻ c&oacute; bệnh n&atilde;o tiến triển hoặc tổn thương ở n&atilde;o.</li>\r\n	<li>Nếu lần trước trẻ từng bị bệnh n&atilde;o (tổn thương ở n&atilde;o) trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m vắc xin ho g&agrave; (ho g&agrave; v&ocirc; b&agrave;o hay nguy&ecirc;n b&agrave;o).</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>C&oacute; nguy cơ bị chảy m&aacute;u khi ti&ecirc;m bắp nếu trẻ bị giảm tiểu cầu hay bị rối loạn đ&ocirc;ng m&aacute;u.</li>\r\n	<li>Trong vắc xin c&oacute; một lượng rất nhỏ glutaraldehyde, neomycin, streptomycin v&agrave; polymycin B, v&igrave; vậy h&atilde;y cẩn trọng đối với những trẻ c&oacute; tiền sử dị ứng với một trong c&aacute;c th&agrave;nh phần n&agrave;y.</li>\r\n	<li>Cần c&acirc;n nhắc cẩn thận khi quyết định d&ugrave;ng tiếp c&aacute;c liều vắc xin chứa ho g&agrave; nếu đ&atilde; từng c&oacute; bất kỳ một trong c&aacute;c triệu chứng sau đ&acirc;y xảy ra:</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Sốt &ge; 40<sup>o</sup>C trong v&ograve;ng 48 giờ sau khi ti&ecirc;m, m&agrave; kh&ocirc;ng phải do một nguy&ecirc;n nh&acirc;n x&aacute;c định n&agrave;o kh&aacute;c.</li>\r\n	<li>Trụy mạch hay giống sốc với giai đoạn giảm trương lực- giảm đ&aacute;p ứng trong v&ograve;ng 38 giờ sau khi ti&ecirc;m ngừa.</li>\r\n	<li>Quấy kh&oacute;c dai dẳng, k&eacute;o d&agrave;i &ge; 3 giờ, xảy ra trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>\r\n	<li>Co giật c&oacute; k&egrave;m theo sốt hay kh&ocirc;ng sốt, xảy ra trong v&ograve;ng 3 ng&agrave;y sau khi ti&ecirc;m.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Nếu trẻ đang/ đ&atilde; c&oacute; vấn đề sức khỏe hay bị dị ứng.</li>\r\n	<li>Nếu trước đ&acirc;y, sau khi ti&ecirc;m vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n (vắc xin uốn v&aacute;n) trẻ bị hội chứng Guillain-Barr&eacute; (nhạy cảm bất thường, liệt) hay vi&ecirc;m d&acirc;y thần kinh c&aacute;nh tay (liệt, đau lan tỏa ở c&aacute;nh tay v&agrave; vai), b&aacute;c sĩ sẽ quyết định c&oacute; tiếp tục d&ugrave;ng vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n nữa hay kh&ocirc;ng.</li>\r\n	<li>Nếu trẻ đang được điều trị bằng thuốc ức chế miễn dịch hoặc bị suy giảm miễn dịch, th&igrave; đ&aacute;p ứng miễn dịch đối với vắc xin sẽ bị giảm.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Nếu đang d&ugrave;ng thuốc hoặc muốn ti&ecirc;m c&ugrave;ng với c&aacute;c vắc xin kh&aacute;c v&agrave;o c&ugrave;ng một thời điểm, h&atilde;y hỏi &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>C&aacute;c phản ứng thường gặp nhất l&agrave; bị k&iacute;ch th&iacute;ch, tại nơi ti&ecirc;m c&oacute; nổi quầng đỏ, nốt cứng lớn hơn 2 cm. C&aacute;c dấu hiệu v&agrave; triệu chứng n&agrave;y thường gặp trong v&ograve;ng 48 giờ sau khi ti&ecirc;m v&agrave; c&oacute; thể k&eacute;o d&agrave;i 48-72 giờ. Ch&uacute;ng thường tự khỏi m&agrave; kh&ocirc;ng cần điều trị đặc hiệu.</li>\r\n	<li>C&aacute;c phản ứng to&agrave;n th&acirc;n: sốt, dễ k&iacute;ch động, buồn ngủ, rối loạn giấc ngủ v&agrave; ăn uống, ti&ecirc;u chảy, &oacute;i mửa, kh&oacute;c nh&egrave; kh&oacute; dỗ v&agrave; k&eacute;o d&agrave;i. Hiếm hơn, c&oacute; thể thấy nổi mề đay, ph&aacute;t ban ngo&agrave;i da, co giật k&egrave;m sốt hoặc kh&ocirc;ng k&egrave;m sốt trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>\r\n	<li>T&igrave;nh trạng giảm trương lực cơ hoặc c&aacute;c đợt giảm trương lực cơ &ndash; giảm phản ứng đ&atilde; được b&aacute;o c&aacute;o.</li>\r\n	<li>Sau khi ti&ecirc;m c&aacute;c vắc xin chứa th&agrave;nh phần Haemophilus influenzae t&yacute;p b, phản ứng sưng ph&ugrave; chi dưới cũng đ&atilde; được b&aacute;o c&aacute;o. Những phản ứng n&agrave;y đ&ocirc;i khi đi k&egrave;m với sốt, đau v&agrave; quấy kh&oacute;c.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8 độ C). Kh&ocirc;ng được đ&oacute;ng băng.</li>\r\n	<li>Vắc xin phải được ho&agrave;n nguy&ecirc;n trước khi ti&ecirc;m, tạo n&ecirc;n hỗn dịch m&agrave;u trắng đục. Sau khi ho&agrave;n nguy&ecirc;n n&ecirc;n sử dụng ngay.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Pentaxim được chỉ định ti&ecirc;m cho trẻ từ 2 th&aacute;ng tuổi&nbsp;đến tr&ograve;n 2 tuổi ph&ograve;ng 5 bệnh: Bạch hầu, Ho g&agrave;, Uốn v&aacute;n, Bại liệt v&agrave; c&aacute;c bệnh do Hib.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p>Lịch ti&ecirc;m vắc xin 5 trong 1 Pentaxim gồm 4 mũi:</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n</li>\r\n	<li>Mũi 2: 1 th&aacute;ng sau mũi 1</li>\r\n	<li>Mũi 3: 1 th&aacute;ng sau mũi 2</li>\r\n	<li>Mũi 4: 1 năm sau mũi 3</li>\r\n</ul>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<p>Khi ti&ecirc;m vắc xin 5 trong 1 Pentaxim kh&ocirc;ng cần điều kiện g&igrave; đặc biệt, Qu&yacute; kh&aacute;ch c&oacute; thể sắp xếp đến&nbsp;<a href=\"https://vnvc.vn/\" rel=\"noopener\" target=\"_blank\">Trung t&acirc;m ti&ecirc;m chủng VNVC</a>&nbsp;để b&aacute;c sĩ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Vắc xin Pentaxim c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, người được ti&ecirc;m c&oacute; thể gặp một số phản ứng nhẹ, k&eacute;o d&agrave;i từ 1- 2 ng&agrave;y:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: nốt quầng đỏ, nốt cứng. C&aacute;c triệu chứng tr&ecirc;n thường gặp trong 48 giờ sau khi ti&ecirc;m v&agrave; c&oacute; thể k&eacute;o d&agrave;i 48 &ndash; 72 giờ.</li>\r\n	<li>To&agrave;n th&acirc;n: trẻ c&oacute; thể sốt, quấy kh&oacute;c, ti&ecirc;u chảy, n&ocirc;n, ch&aacute;n ăn, buồn ngủ, ph&aacute;t ban.</li>\r\n</ul>', 'ncc5', '9', 3, '30', 1, 'images/hinhanhvaccine/20240626_161708.jpg'),
('vcPrevenar13', 'Prevenar 13', 'Bỉ', '1290000', 200, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p><strong>Vắc xin Prevenar 13</strong>&nbsp;l&agrave;&nbsp;<a href=\"https://vnvc.vn/vac-xin-phe-cau/\" rel=\"noopener\" target=\"_blank\">vắc xin phế cầu</a>, ph&ograve;ng c&aacute;c bệnh phế cầu khuẩn x&acirc;m lấn g&acirc;y nguy hiểm cho trẻ em v&agrave; người lớn như vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o, nhiễm khuẩn huyết (nhiễm tr&ugrave;ng m&aacute;u), vi&ecirc;m tai giữa cấp t&iacute;nh,&hellip; do 13 chủng phế cầu khuẩn Streptococcus Pneumoniae g&acirc;y ra (type 1, 3, 4, 5, 6A, 6B, 7F, 9V, 14, 18C, 19A, 19F v&agrave; 23F).&nbsp;</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Prevenar-13 được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học &ndash; Pfizer (Mỹ). Prevenar-13 được sản xuất tại Bỉ.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Vắc xin Prevenar-13 được chỉ định ti&ecirc;m bắp (v&ugrave;ng&nbsp;<a href=\"https://www.kenhub.com/en/library/anatomy/the-deltoid-muscle\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">cơ delta</a>) với liều 0.5ml</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng d&ugrave;ng vắc xin Prevenar-13 trong thai kỳ.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m vắc xin Prevenar-13 với người qu&aacute; mẫn cảm với th&agrave;nh phần trong vắc xin hoặc với độc tố bạch hầu.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Prevenar-13 kh&ocirc;ng được ti&ecirc;m nội mạch.</li>\r\n	<li>N&ecirc;n ho&agrave;n tất ph&aacute;c đồ với c&ugrave;ng một loại vắc xin Synflorix hoặc Prevenar-13.</li>\r\n	<li>Trong trường hợp bất khả kh&aacute;ng, c&oacute; thể chuyển đổi vắc xin Synflorix v&agrave; Prevenar-13 v&agrave;o bất cứ thời điểm n&agrave;o trong lịch tr&igrave;nh ti&ecirc;m chủng.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m vắc xin Prevenar-13 ở bệnh nh&acirc;n giảm tiểu cầu hoặc bất kỳ rối loạn đ&ocirc;ng m&aacute;u n&agrave;o.</li>\r\n	<li>Trẻ em từ 2 tuổi đ&atilde; ho&agrave;n th&agrave;nh ph&aacute;c đồ ti&ecirc;m Synflorix trước đ&oacute; c&oacute; thể được khuyến c&aacute;o ti&ecirc;m 1 liều Prevenar-13 để k&iacute;ch th&iacute;ch sinh miễn dịch với 3 type huyết thanh bổ sung. Mũi ti&ecirc;m Prevenar-13 bổ sung được ti&ecirc;m c&aacute;ch mũi Synflorix cuối c&ugrave;ng 2 th&aacute;ng.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng c&oacute; chống chỉ định về phối hợp vắc xin giữa Prevenar-13 với c&aacute;c vắc xin kh&aacute;c.</li>\r\n	<li>Nếu phải ti&ecirc;m th&ecirc;m 1 loại vắc xin kh&aacute;c c&ugrave;ng l&uacute;c với Prevenar-13, vắc xin cần được ti&ecirc;m ở một vị tr&iacute; kh&aacute;c.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Phản ứng tại chỗ ti&ecirc;m: Nổi ban đỏ, chai cứng, sưng đau, tăng nhạy cảm tại chỗ ti&ecirc;m.</li>\r\n	<li>C&aacute;c phản ứng to&agrave;n th&acirc;n kh&aacute;c: Sốt, đau đầu, buồn ngủ, giảm cảm gi&aacute;c th&egrave;m ăn, n&ocirc;n mửa, ti&ecirc;u chảy.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<p>Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8 độ C). Kh&ocirc;ng được đ&oacute;ng băng.</p>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Prevenar-13 (Bỉ) được chỉ định cho trẻ từ 6 tuần tuổi trở l&ecirc;n v&agrave; người lớn.</p>\r\n\r\n<p><strong><em>* Lưu &yacute;:&nbsp;</em></strong><em>&nbsp;</em><em><strong>Kh&aacute;ch h&agrave;ng đ&atilde; ti&ecirc;m Pneumo23</strong></em></p>\r\n\r\n<ul>\r\n	<li>Đối với Kh&aacute;ch h&agrave;ng c&oacute; sức khỏe b&igrave;nh thường: khoảng c&aacute;ch tối thiểu giữa Prevenar-13 v&agrave; Pneumo23 l&agrave; 1 năm (kh&ocirc;ng ti&ecirc;m đồng thời, n&ecirc;n ho&agrave;n th&agrave;nh lịch ti&ecirc;m vắc xin Prevenar 13 trước).</li>\r\n	<li>Đối với Kh&aacute;ch h&agrave;ng c&oacute; bệnh l&yacute; hoặc c&oacute; t&igrave;nh trạng suy giảm miễn dịch l&agrave;m tăng nguy cơ mắc bệnh phế cầu x&acirc;m lấn cần tham khảo hướng dẫn của b&aacute;c sĩ kh&aacute;m s&agrave;ng lọc.</li>\r\n</ul>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p><strong><em>Từ 6 tuần tuổi đến dưới 7 th&aacute;ng tuổi:&nbsp;&nbsp;</em></strong>Lịch ti&ecirc;m gồm 4 mũi:</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</li>\r\n	<li>Mũi 2: c&aacute;ch mũi 1 l&agrave; 1 th&aacute;ng.</li>\r\n	<li>Mũi 3: c&aacute;ch mũi 2 l&agrave; 1 th&aacute;ng.</li>\r\n	<li>Mũi 4 (mũi nhắc lại): tối thiểu 8 th&aacute;ng kể từ mũi thứ 3</li>\r\n</ul>\r\n\r\n<p>(Mũi 4 c&aacute;ch mũi 3 tối thiểu 2 th&aacute;ng, khi trẻ 11-15 th&aacute;ng tuổi).</p>\r\n\r\n<p><strong><em>Từ 7 th&aacute;ng đến dưới 12 th&aacute;ng tuổi (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute;):&nbsp;&nbsp;</em></strong>Lịch ti&ecirc;m gồm 3 mũi:</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</li>\r\n	<li>Mũi 2: c&aacute;ch mũi 1 l&agrave; 1 th&aacute;ng.</li>\r\n	<li>Mũi 3 (mũi nhắc lại): c&aacute;ch mũi 2 tối thiểu 6 th&aacute;ng.</li>\r\n</ul>\r\n\r\n<p>(Mũi 3 c&aacute;ch mũi 2 tối thiểu 2 th&aacute;ng, khi trẻ tr&ecirc;n 1 tuổi)</p>\r\n\r\n<p><strong><em>Từ 12 th&aacute;ng đến dưới 24 th&aacute;ng tuổi (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute;):</em>&nbsp;&nbsp;</strong>Lịch ti&ecirc;m gồm 2 mũi:</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</li>\r\n	<li>Mũi 2: c&aacute;ch mũi 1 tối thiểu 2 th&aacute;ng.</li>\r\n</ul>\r\n\r\n<p><strong><em>Từ 24 th&aacute;ng đến người lớn (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute; hoặc chưa từng ti&ecirc;m vắc xin Pneumo 23):</em></strong>&nbsp; Lịch ti&ecirc;m 01 mũi.</p>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<ul>\r\n	<li><strong>Bị nhiễm Covid-19 rồi c&oacute; ti&ecirc;m Prevenar-13 được kh&ocirc;ng?</strong></li>\r\n</ul>\r\n\r\n<p>Sau khi khỏi bệnh Covid-19, ho&agrave;n to&agrave;n c&oacute; thể ti&ecirc;m được c&aacute;c vắc xin. B&aacute;c sĩ sẽ kh&aacute;m s&agrave;ng lọc v&agrave; c&oacute; chỉ định ph&ugrave; hợp.&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Người đang c&oacute; bị bệnh l&yacute; nền c&oacute; ti&ecirc;m được Prevenar-13 kh&ocirc;ng?</strong></li>\r\n</ul>\r\n\r\n<p>Trước khi ti&ecirc;m chủng, bạn sẽ được b&aacute;c sĩ thăm kh&aacute;m, tư vấn đầy đủ. V&igrave; vậy, cần th&ocirc;ng tin cho b&aacute;c sĩ về bệnh l&yacute; v&agrave; loại thuốc bạn đang sử dụng. T&ugrave;y từng trường hợp cụ thể, b&aacute;c sĩ sẽ đưa ra những chỉ định ph&ugrave; hợp.&nbsp;</p>\r\n\r\n<h2>5. Khoảng c&aacute;ch với c&aacute;c vắc xin kh&aacute;c</h2>\r\n\r\n<p><em><strong>Đối với Menactra</strong></em></p>\r\n\r\n<p>Người c&oacute; nguy cơ cao mắc phế cầu x&acirc;m lấn như người suy giảm chức năng l&aacute;ch, cắt l&aacute;ch hoặc suy giảm miễn dịch/thiếu hụt bổ thể&hellip; cần tham khảo hướng dẫn của b&aacute;c sĩ kh&aacute;m s&agrave;ng lọc.</p>\r\n\r\n<p><strong><em>* Lưu &yacute;: Kh&aacute;ch h&agrave;ng đ&atilde; ti&ecirc;m Pneumo23:</em></strong></p>\r\n\r\n<ul>\r\n	<li>Khoảng c&aacute;ch tối thiểu giữa Prevenar-13 v&agrave; Pneumo23 l&agrave; 1 năm (kh&ocirc;ng ti&ecirc;m đồng thời, n&ecirc;n ho&agrave;n th&agrave;nh lịch ti&ecirc;m vắc xin Prevenar 13 trước).</li>\r\n	<li>Đối với Kh&aacute;ch h&agrave;ng c&oacute; bệnh l&yacute; hoặc c&oacute; t&igrave;nh trạng suy giảm miễn dịch l&agrave;m tăng nguy cơ mắc bệnh phế cầu x&acirc;m lấn: tham khảo hướng dẫn của b&aacute;c sĩ kh&aacute;m s&agrave;ng lọc.</li>\r\n</ul>\r\n\r\n<h2>6. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Vắc xin Prevenar-13 c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, người được ti&ecirc;m c&oacute; thể gặp một số phản ứng nhẹ, k&eacute;o d&agrave;i từ 1- 2 ng&agrave;y:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: ban đỏ, chai cứng, sưng đau, tăng nhạy cảm tại chỗ ti&ecirc;m.</li>\r\n	<li>To&agrave;n th&acirc;n: sốt, đau đầu, buồn ngủ, giảm cảm gi&aacute;c th&egrave;m ăn, n&ocirc;n mửa, ti&ecirc;u chảy.</li>\r\n</ul>', 'ncc3', '26', 3, '30', 1, 'images/hinhanhvaccine/20240626_161022.jpg'),
('vcQuimihib', 'Quimihib', 'Cuba', '315000', 67, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin Quimihib ph&ograve;ng ngừa bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o mủ do t&aacute;c nh&acirc;n Haemophilus Influenzae type b g&acirc;y ra ở trẻ nhỏ.</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin QuimiHib được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học&nbsp;<a href=\"https://www.cigb.edu.cu/en/home/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">C.I.G.B</a>&nbsp;&ndash; Cuba.</li>\r\n</ul>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<ul>\r\n	<li>Ti&ecirc;m bắp.</li>\r\n	<li>Ti&ecirc;m v&agrave;o v&ugrave;ng trước b&ecirc;n đ&ugrave;i của trẻ dưới 2 tuổi.</li>\r\n	<li>Ti&ecirc;m v&agrave;o v&ugrave;ng cơ delta đối với trẻ tr&ecirc;n 2 tuổi.</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng ti&ecirc;m QuimiHib cho đối tượng dị ứng, mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m cho đối tượng đang sốt cao hoặc bệnh cấp t&iacute;nh.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m v&agrave;o mạch m&aacute;u, tĩnh mạch trong bất cứ trường hợp n&agrave;o.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Như mọi vắc xin kh&aacute;c, trước ti&ecirc;m&nbsp;vắc xin QuimiHib&nbsp;lu&ocirc;n cần phải chuẩn bị sẵn adrenalin v&agrave; corticosteroids để dự phòng khi xảy ra c&aacute;c phản ứng qu&aacute; mẫn, sốc phản vệ.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m c&aacute;c liều Quimi-Hib cho c&aacute;c đối tượng c&oacute; biểu hiện qu&aacute; mẫn khi ti&ecirc;m c&aacute;c liều trước đ&oacute;.</li>\r\n	<li>Ti&ecirc;m cho đối tượng suy giảm miễn dịch hoặc đang sử dụng c&aacute;c thuốc ức chế miễn dịch, c&oacute; thể cho đ&aacute;p ứng hạn chế hoặc kh&ocirc;ng h&igrave;nh th&agrave;nh đủ miễn dịch.</li>\r\n	<li>Quimi-Hib c&oacute; thể tạo kh&aacute;ng thể kh&aacute;ng uốn v&aacute;n nhưng kh&ocirc;ng thể thay thế vắc xin DTP.</li>\r\n	<li>Kh&ocirc;ng sử dụng vắc xin cho phụ nữ c&oacute; thai hoặc đang cho con b&uacute;.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin dung nạp tốt, &iacute;t g&acirc;y dị ứng ở trẻ sơ sinh. Một số t&aacute;c dụng kh&ocirc;ng mong muốn, ghi nhận c&oacute; thể xảy ra như:</li>\r\n	<li>C&aacute;c phản ứng tại chỗ: Đỏ, đau, ngứa, cứng da tại chỗ ti&ecirc;m.</li>\r\n	<li>Phản ứng to&agrave;n th&acirc;n: Sốt nhẹ, đau đầu, mệt mỏi, dễ k&iacute;ch ứng, ban da, kh&oacute; chịu, n&ocirc;n, buồn n&ocirc;n.</li>\r\n	<li>C&aacute;c phản ứng thường nhẹ v&agrave; tự khỏi trong 72 giờ sau khi ti&ecirc;m.</li>\r\n	<li>Phải th&ocirc;ng b&aacute;o ngay cho b&aacute;c sĩ khi gặp phải những t&aacute;c dụng kh&ocirc;ng mong muốn sau khi ti&ecirc;m vắc xin.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>C&oacute; thể d&ugrave;ng đồng thời Quimi-Hib với c&aacute;c vắc xin như: bạch hầu, ho g&agrave;, uốn v&aacute;n, vi&ecirc;m gan B. Tuy nhi&ecirc;n phải ti&ecirc;m kh&aacute;c bơm kim ti&ecirc;m v&agrave; kh&aacute;c vị tr&iacute; ti&ecirc;m.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin được bảo quản ở nhiệt độ từ 2 đến 8 độ C. Kh&ocirc;ng được để đ&ocirc;ng đ&aacute; vắc xin. Loại bỏ vắc xin nếu bị đ&ocirc;ng đ&aacute;. Vắc xin cần được ti&ecirc;m ngay sau khi mở lọ.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin QuimiHib d&agrave;nh cho trẻ em từ 2 th&aacute;ng tuổi đến 15 tuổi.</p>\r\n\r\n<h2>3. Phác đ&ocirc;̀, lịch ti&ecirc;m</h2>\r\n\r\n<p>Lịch ti&ecirc;m cho trẻ dưới hoặc bằng 12 th&aacute;ng tuổi:</p>\r\n\r\n<ul>\r\n	<li>Ti&ecirc;m 3 mũi cơ bản khi trẻ được 2 &ndash; 4 &ndash; 6 th&aacute;ng tuổi (phải đảm bảo mũi sau c&aacute;ch mũi trước tối thiểu 8 tuần).</li>\r\n	<li>Mũi nhắc thứ 4 ti&ecirc;m l&uacute;c trẻ từ 15 &ndash; 18 th&aacute;ng tuổi (mũi 4 phải c&aacute;ch mũi 3 tối thiểu 2 th&aacute;ng).</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Lịch ti&ecirc;m cho trẻ tr&ecirc;n 12 th&aacute;ng tuổi: Chỉ cần ti&ecirc;m duy nhất 1 mũi QuimiHib.</li>\r\n</ul>\r\n\r\n<h2>4. Khoảng c&aacute;ch với vắc xin kh&aacute;c</h2>\r\n\r\n<p>Khoảng c&aacute;ch giữa&nbsp;Quimihib&nbsp;v&agrave; c&aacute;c vắc xin như 5in1 v&agrave; 6in1 c&aacute;ch nhau &iacute;t nhất 1 th&aacute;ng.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Sau khi ti&ecirc;m vắc xin ph&ograve;ng c&aacute;c bệnh do Hib, trẻ hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể, cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đỏ, sưng, đau v&agrave; c&oacute; thể ngứa tại vị tr&iacute; ti&ecirc;m</li>\r\n	<li>To&agrave;n th&acirc;n: Sốt nhẹ, quấy kh&oacute;c, biếng ăn, buồn n&ocirc;n, n&ocirc;n , ti&ecirc;u chảy</li>\r\n</ul>\r\n\r\n<p>C&aacute;c triệu chứng nhẹ v&agrave; tự khỏi trong 1-3 ng&agrave;y</p>', 'ncc6', '11', 3, '90', 1, 'images/hinhanhvaccine/20240626_162935.jpg'),
('vcRotarix', 'Rotarix', 'Bỉ', '825000', 90, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Rotarix l&agrave; vắc xin sống, giảm độc lực được chỉ định cho trẻ từ 6 tuần tuổi ph&ograve;ng vi&ecirc;m dạ d&agrave;y &ndash; ruột do Rotavirus tu&yacute;p huyết thanh G1 v&agrave; kh&ocirc;ng phải G1. Mặc d&ugrave; trong th&agrave;nh phần chỉ c&oacute; 1 tu&yacute;p G1P tuy nhi&ecirc;n vắc xin c&oacute; khả năng bảo vệ ch&eacute;o tất cả c&aacute;c tu&yacute;p G1 v&agrave; kh&ocirc;ng phải G1 (G2, G3, G4, G9).</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Rotarix được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học &ndash; GlaxoSmithKline (Bỉ).</p>\r\n\r\n<h3><strong>Đường uống</strong></h3>\r\n\r\n<ul>\r\n	<li>Chỉ d&ugrave;ng đường uống.</li>\r\n	<li>Vắc xin Rotarix c&oacute; khả năng b&aacute;m d&iacute;nh rất tốt v&igrave; vậy sau khi uống nếu trẻ c&oacute; n&ocirc;n trớ th&igrave; cũng kh&ocirc;ng cần uống liều kh&aacute;c. Tuy nhi&ecirc;n nếu x&aacute;c định rằng đ&atilde; bị n&ocirc;n trớ phần lớn vắc xin th&igrave; c&oacute; thể uống lại.</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng d&ugrave;ng vắc xin Rotarix cho trẻ đ&atilde; qu&aacute; mẫn cảm ở lần uống đầu ti&ecirc;n hoặc với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin.</li>\r\n	<li>Kh&ocirc;ng d&ugrave;ng cho trẻ c&oacute; dị tật bẩm sinh về đường ti&ecirc;u h&oacute;a v&igrave; c&oacute; thể dẫn đến lồng ruột (như&nbsp;<a href=\"https://www.msdmanuals.com/professional/gastrointestinal-disorders/diverticular-disease/meckel-diverticulum\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">t&uacute;i thừa Meckel</a>).</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>N&ecirc;n tạm ho&atilde;n việc d&ugrave;ng vắc xin nếu trẻ đang bị sốt hoặc rối loạn ti&ecirc;u h&oacute;a v&agrave; bị n&ocirc;n trớ. Tuy nhi&ecirc;n nếu trẻ bị cảm lạnh th&igrave; vẫn c&oacute; thể d&ugrave;ng b&igrave;nh thường.</li>\r\n	<li>Đối với trẻ vừa uống vắc xin th&igrave; cần lưu &yacute; giữ vệ sinh c&aacute; nh&acirc;n như: rửa tay sau khi thay t&atilde;&hellip;</li>\r\n	<li>Chưa c&oacute; dữ liệu về độ an to&agrave;n v&agrave; hiệu lực của vắc xin đối với những trẻ mắc bệnh đường ti&ecirc;u h&oacute;a. N&ecirc;n thận trọng v&agrave; hỏi &yacute; kiến b&aacute;c sĩ khi sử dụng cho c&aacute;c đối tượng n&agrave;y v&igrave; vắc xin c&oacute; thể l&agrave;m bệnh của trẻ trầm trọng hơn.</li>\r\n	<li>Chưa c&oacute; dữ liệu nghi&ecirc;n cứu l&acirc;m s&agrave;ng tr&ecirc;n trẻ mắc bệnh suy giảm miễn dịch ti&ecirc;n ph&aacute;t hoặc thứ ph&aacute;t (bao gồm cả trẻ nhiễm HIV dương t&iacute;nh).</li>\r\n	<li>Cũng như c&aacute;c vắc xin kh&aacute;c, đ&aacute;p ứng miễn dịch c&oacute; thể kh&ocirc;ng đạt được ở tất cả c&aacute;c trẻ uống vắc xin. Rotarix kh&ocirc;ng ngừa được c&aacute;c trường hợp vi&ecirc;m dạ d&agrave;y ruột m&agrave; kh&ocirc;ng phải do Rotavirus.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Bị k&iacute;ch th&iacute;ch, ăn k&eacute;m hơn do mất cảm gi&aacute;c ngon miệng, c&oacute; thể n&ocirc;n trớ, đau bụng, đầy hơi hoặc đi ngo&agrave;i.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin Rotarix c&oacute; thể uống c&ugrave;ng ng&agrave;y v&agrave; kh&ocirc;ng c&oacute; khoảng c&aacute;ch với c&aacute;c vắc xin kh&aacute;c. Khi uống c&ugrave;ng với vắc xin bại liệt dạng uống (OPV) th&igrave; đ&aacute;p ứng miễn dịch của vắc xin c&oacute; ảnh hưởng nhưng vẫn giữ hiệu quả bảo vệ &ndash; kh&ocirc;ng l&agrave;m ảnh hưởng đến khả năng bảo vệ chống lại vi khuẩn g&acirc;y vi&ecirc;m dạ d&agrave;y ruột.&nbsp;</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin đ&ocirc;ng kh&ocirc; được bảo quản ở nhiệt độ 2-8 độ C, tr&aacute;nh &aacute;nh s&aacute;ng.</li>\r\n	<li>Dung m&ocirc;i ho&agrave;n nguy&ecirc;n c&oacute; thể bảo quản ở 2-8 độ C hoặc ở nhiệt độ ph&ograve;ng (&lt;37 độ C).</li>\r\n	<li>Sau khi ho&agrave;n nguy&ecirc;n, vắc xin được sử dụng ngay hoặc bảo quản trong tủ lạnh từ 2-8 độ C trong v&ograve;ng 24 giờ. Sau 24 giờ phải loại bỏ vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Rotarix được chỉ định chủng ngừa cho trẻ từ 6 tuần tuổi ph&ograve;ng tr&aacute;nh nguy cơ nhiễm virus Rota &ndash; nguy&ecirc;n nh&acirc;n g&acirc;y ti&ecirc;u chảy cấp ở trẻ nhỏ.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch uống</h2>\r\n\r\n<p>Vắc xin Rotarix (Bỉ) c&oacute; lịch uống 2 liều li&ecirc;n tiếp c&aacute;ch nhau tối thiểu 4 tuần.</p>\r\n\r\n<ul>\r\n	<li>Liều đầu ti&ecirc;n c&oacute; thể uống sớm l&uacute;c 6 tuần tuổi.</li>\r\n	<li>Cần ho&agrave;n th&agrave;nh ph&aacute;c đồ đến tr&ograve;n 24 tuần tuổi (Muộn nhất l&agrave; đến tr&ograve;n 6 th&aacute;ng tuổi).</li>\r\n</ul>\r\n\r\n<h2>4. Điều kiện trước uống</h2>\r\n\r\n<p>Một số lưu &yacute; khi sử dụng vắc xin Rota ph&ograve;ng ti&ecirc;u chảy cấp như sau:</p>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng d&ugrave;ng vắc xin cho trẻ c&oacute; tiền sử lồng ruột, dị tật bẩm sinh đường ti&ecirc;u ho&aacute;, trẻ bị suy giảm miễn dịch kết hợp nghi&ecirc;m trọng.</li>\r\n	<li>N&ecirc;n ho&agrave;n tất ph&aacute;c đồ với c&ugrave;ng một loại vắc xin.</li>\r\n	<li>Trong trường hợp bất khả kh&aacute;ng, c&oacute; thể chuyển đổi giữa vắc xin Rotavin, Rotarix v&agrave; Rotateq trong liệu tr&igrave;nh uống vắc xin với điều kiện: Tổng số liều uống vắc xin l&agrave; 3 liều (Trường hợp n&agrave;y Kh&aacute;ch h&agrave;ng cần l&agrave;m một số thủ tục trước khi chỉ định uống).</li>\r\n	<li>Kh&ocirc;ng c&oacute; chống chỉ định về phối hợp vắc xin giữa Rotavin, Rotarix, Rotateq v&agrave; c&aacute;c vắc xin kh&aacute;c.</li>\r\n</ul>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Vắc xin Rotarix c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, sau khi uống vắc xin, trẻ hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể. Cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>To&agrave;n th&acirc;n: rối loạn ti&ecirc;u h&oacute;a v&agrave; thường tự khỏi sau v&agrave;i ng&agrave;y.</li>\r\n	<li>Nếu đi ngo&agrave;i ph&acirc;n nước nhiều lần, n&ocirc;n nhiều, c&oacute; dấu hiệu mất nước n&ecirc;n kh&aacute;m lại ngay tại cơ sở y tế.</li>\r\n</ul>', 'ncc6', '5', 2, '28', 1, 'images/hinhanhvaccine/20240626_161909.jpg'),
('vcSTAMARIL', 'STAMARIL', 'Pháp', '665000', 98, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p><strong>Vắc xin Stamaril</strong>&nbsp;l&agrave; vắc xin duy nhất cung cấp miễn dịch bảo vệ hiệu quả cao v&agrave; tạo miễn dịch chủ động suốt đời khỏi virus thuộc họ Flaviviridae g&acirc;y bệnh Sốt v&agrave;ng nguy hiểm.&nbsp;</p>\r\n\r\n<p><strong>Nguồn gốc:</strong></p>\r\n\r\n<p>Vắc xin Stamaril được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học Sanofi Pasteur (Ph&aacute;p).</p>\r\n\r\n<p><strong>Đường ti&ecirc;m:</strong></p>\r\n\r\n<p>Vắc xin Stamaril được chỉ định tốt nhất n&ecirc;n ti&ecirc;m dưới da.</p>\r\n\r\n<p>Ti&ecirc;m bắp được khuyến nghị như sau:</p>\r\n\r\n<ul>\r\n	<li>Trẻ dưới 12 th&aacute;ng tuổi: Ti&ecirc;m mặt trước của đ&ugrave;i.</li>\r\n	<li>Trẻ từ 12 &ndash; 35 th&aacute;ng tuổi: Ti&ecirc;m mặt trước của đ&ugrave;i hoặc cơ delta.</li>\r\n	<li>Trẻ từ 36 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người lớn: Ti&ecirc;m ở cơ delta.</li>\r\n</ul>\r\n\r\n<p><strong>Chống chỉ định:</strong></p>\r\n\r\n<ul>\r\n	<li>Vắc xin ph&ograve;ng bệnh sốt v&agrave;ng chống chỉ định với những người c&oacute; tiền sử dị ứng với protein của trứng g&agrave;, thịt g&agrave;, v&agrave; c&aacute;c th&agrave;nh phần c&oacute; trong vắc xin.</li>\r\n	<li>Chống chỉ định với người bị suy giảm, rối loạn chức năng tuyến ức.</li>\r\n	<li>Chống chỉ định với người suy giảm miễn dịch (do bẩm sinh, mắc phải, thuốc, xạ trị), người nhiễm HIV.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m vắc xin cho phụ nữ c&oacute; thai v&agrave; cho con b&uacute;.</li>\r\n</ul>\r\n\r\n<p><strong>Thận trọng khi sử dụng:</strong></p>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng khuyến c&aacute;o ti&ecirc;m cho trẻ dưới 9 th&aacute;ng tuổi v&agrave; người lớn tr&ecirc;n 60 tuổi.</li>\r\n	<li>Kh&ocirc;ng c&oacute; chống chỉ định về phối hợp giữa vắc xin Stamaril v&agrave; c&aacute;c vắc xin kh&aacute;c. Vắc xin Stamaril c&oacute; thể chỉ định c&ugrave;ng thời điểm hoặc chỉ định c&aacute;ch xa nhau 28 ng&agrave;y khi phối hợp với c&aacute;c vắc xin sống kh&aacute;c.</li>\r\n</ul>\r\n\r\n<p><strong>Tương t&aacute;c thuốc:</strong></p>\r\n\r\n<ul>\r\n	<li>Stamaril kh&ocirc;ng được trộn lẫn với bất kỳ loại vắc xin hoặc dược phẩm n&agrave;o kh&aacute;c trong c&ugrave;ng một ống ti&ecirc;m.</li>\r\n	<li>Nếu phải ti&ecirc;m th&ecirc;m 1 loại vắc xin kh&aacute;c c&ugrave;ng l&uacute;c với Stamaril, vắc xin cần được ti&ecirc;m ở một vị tr&iacute; ri&ecirc;ng biệt (ti&ecirc;m sang 1 chi kh&aacute;c).</li>\r\n</ul>\r\n\r\n<p><strong>T&aacute;c dụng kh&ocirc;ng mong muốn:</strong></p>\r\n\r\n<ul>\r\n	<li>Đau tại vết ti&ecirc;m, đ&ocirc;i khi sưng v&agrave; ửng đỏ.</li>\r\n	<li>Sốt nhẹ v&agrave; n&ocirc;n.</li>\r\n</ul>\r\n\r\n<p><strong>Bảo quản:</strong></p>\r\n\r\n<ul>\r\n	<li>Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8 độ C). Kh&ocirc;ng được đ&oacute;ng băng.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Stamaril được chỉ định ti&ecirc;m cho trẻ từ 9 th&aacute;ng tuổi&nbsp;đến người lớn 60 tuổi ph&ograve;ng bệnh sốt v&agrave;ng.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p><em>Lịch ti&ecirc;m cơ bản:</em>&nbsp;Ti&ecirc;m 1 mũi duy nhất vắc xin Stamaril 0.5ml (sau khi đ&atilde; pha hồi chỉnh).</p>\r\n\r\n<p><em>Lịch ti&ecirc;m nhắc:</em>&nbsp;C&acirc;n nhắc ti&ecirc;m nhắc lại vắc xin Stamaril 0.5ml mỗi 10 năm đối với:</p>\r\n\r\n<ul>\r\n	<li>Người kh&ocirc;ng tạo đủ đ&aacute;p ứng miễn dịch sau khi ti&ecirc;m mũi đầu.</li>\r\n	<li>Khuyến c&aacute;o ch&iacute;nh thức của c&aacute;c Cơ quan Quản l&yacute; Y tế địa phương v&agrave; điều kiện nhập cảnh ở một số Quốc gia.</li>\r\n</ul>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;Ti&ecirc;m vắc xin &iacute;t nhất 10 ng&agrave;y trước khi đi v&agrave;o v&ugrave;ng lưu h&agrave;nh dịch bệnh.</p>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<p>Khi ti&ecirc;m vắc xin Stamaril kh&ocirc;ng cần điều kiện g&igrave; đặc biệt, Qu&yacute; kh&aacute;ch h&agrave;ng c&oacute; thể sắp xếp đến Trung t&acirc;m VNVC để b&aacute;c sĩ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Vắc xin Stamaril c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, sau khi ti&ecirc;m vắc xin, người ti&ecirc;m c&oacute; thể c&oacute; cảm gi&aacute;c buồn n&ocirc;n, n&ocirc;n v&agrave; c&aacute;c triệu chứng hiếm gặp: đau đầu, đau bụng, ti&ecirc;u chảy, sốt&hellip; C&aacute;c triệu chứng n&agrave;y tự khỏi m&agrave; kh&ocirc;ng cần điều trị.</p>', 'ncc3', '30', 2, '180', 1, 'images/hinhanhvaccine/20240626_163718.jpg');
INSERT INTO `vaccine` (`mavc`, `tenvc`, `nuocsx`, `gia`, `soluong`, `thongtinvc`, `mancc`, `benh_nhombenh`, `solieu`, `khoangcachmuitiem`, `trangthaivc`, `hinhanhvc`) VALUES
('vcTyphoidVI', 'Typhoid VI', 'Việt Nam', '265000', 65, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin Typhoid VI ph&ograve;ng ngừa bệnh Thương h&agrave;n (bệnh về đường ti&ecirc;u h&oacute;a) g&acirc;y ra bởi vi khuẩn thương h&agrave;n (Salmonella typhi) cho trẻ từ tr&ecirc;n 2 tuổi v&agrave; người lớn.&nbsp;</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin thương h&agrave;n Typhoid Vi được sản xuất bởi nh&agrave; sản xuất vắc xin v&agrave; sinh phẩm uy t&iacute;n tại Việt Nam &ndash; Viện Pasteur Đ&agrave; Lạt (<a href=\"https://davac.com.vn/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">DAVAC</a>).</li>\r\n</ul>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin ph&ograve;ng bệnh thương h&agrave;n Typhoid Vi được chỉ định ti&ecirc;m dưới da hoặc ti&ecirc;m bắp với liều 0.5ml.</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng sử dụng Typhoid Vi cho trường hợp có ti&ecirc;̀n sử dị ứng với m&ocirc;̣t trong các thành ph&acirc;̀n của vắc xin.</li>\r\n	<li>Phụ nữ đang mang thai, trường hợp bắt bu&ocirc;̣c phải ti&ecirc;m, c&acirc;̀n hỏi ý ki&ecirc;́n của bác sĩ.</li>\r\n	<li>Trong trường hợp bị sốt hoặc mắc bệnh cấp t&iacute;nh, n&ecirc;n ho&atilde;n ti&ecirc;m vắc xin.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng khuyến c&aacute;o ti&ecirc;m cho trẻ em dưới 2 tuổi.</li>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m theo đường nội mạch.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Phản ứng thường nhẹ, nhanh ch&oacute;ng biến mất. Khi ti&ecirc;m vắc xin, tại chỗ ti&ecirc;m c&oacute; thể sưng đỏ. C&oacute; triệu chứng sốt nhẹ v&agrave; sẽ hết sau 24h kể từ khi ti&ecirc;m vắc xin.</li>\r\n	<li>Hiếm gặp phản ứng phản vệ.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin Typhoid Vi kh&ocirc;ng được trộn lẫn với bất cứ vắc xin hoặc dược phẩm n&agrave;o trong c&ugrave;ng 1 ống ti&ecirc;m.</li>\r\n	<li>Nếu phải ti&ecirc;m th&ecirc;m 1 loại vắc xin kh&aacute;c c&ugrave;ng l&uacute;c với Typhoid Vi, vắc xin cần được ti&ecirc;m ở 1 vị tr&iacute; ri&ecirc;ng biệt.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin được bảo quản ở nhiệt độ từ 2 độ C đến 8 độ C. Kh&ocirc;ng được đ&oacute;ng băng.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin thương h&agrave;n được chỉ định cho trẻ từ tr&ecirc;n 2 tuổi (2 tuổi 1 ng&agrave;y) trở l&ecirc;n v&agrave; người lớn.</p>\r\n\r\n<h2>3. Phác đ&ocirc;̀, lịch ti&ecirc;m</h2>\r\n\r\n<ul>\r\n	<li>Lịch ti&ecirc;m 01 mũi.</li>\r\n	<li>Ti&ecirc;m nhắc: mỗi 3 năm nếu c&oacute; nguy cơ nhiễm bệnh.</li>\r\n</ul>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<p>Vắc xin Thương h&agrave;n của Việt Nam chỉ ti&ecirc;m cho phụ nữ mang thai &amp; cho con b&uacute; khi thật sự cần thiết, cần c&oacute; sự thăm kh&aacute;m v&agrave; tư vấn của B&aacute;c sĩ.</p>\r\n\r\n<h2>5. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Sau khi ti&ecirc;m vắc xin ph&ograve;ng vắc xin thương h&agrave;n, người ti&ecirc;m thường gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể. Cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Phản ứng tại chỗ: sưng, đau, c&oacute; quầng đỏ tại vết ti&ecirc;m.</li>\r\n	<li>Phản ứng to&agrave;n th&acirc;n: sốt, đau đầu, đau khớp</li>\r\n</ul>', 'ncc7', '27', 1, '1095', 1, 'images/hinhanhvaccine/20240626_163948.jpg'),
('vcuốnvánhấpphụ', 'uốn ván hấp phụ', 'Việt Nam', '149000', 46, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin uốn v&aacute;n hấp phụ (TT) gi&uacute;p tạo miễn dịch chủ động ph&ograve;ng bệnh uốn v&aacute;n cho người lớn v&agrave; trẻ em.&nbsp;</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin uốn v&aacute;n hấp phụ (TT) được nghi&ecirc;n cứu v&agrave; sản xuất bởi&nbsp;<a href=\"https://ivac.com.vn/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Viện vắc xin v&agrave; sinh phẩm y tế Nha Trang IVAC</a>&nbsp;&ndash; Việt Nam.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin uốn v&aacute;n hấp phụ (TT) được chỉ định ti&ecirc;m bắp s&acirc;u, liều ti&ecirc;m 0,5ml.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m tĩnh mạch trong bất cứ trường hợp n&agrave;o.</li>\r\n	<li>Lắc tan đều trước khi ti&ecirc;m.</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng ti&ecirc;m cho người dị ứng, qu&aacute; mẫn với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m cho đối tượng c&oacute; c&aacute;c biểu hiện dị ứng ở lần ti&ecirc;m vắc xin trước.</li>\r\n	<li>Kh&ocirc;ng d&ugrave;ng cho người c&oacute; c&aacute;c dấu hiệu, triệu chứng thần kinh sau khi ti&ecirc;m c&aacute;c liều trước đ&oacute;.</li>\r\n	<li>Ho&atilde;n ti&ecirc;m với c&aacute;c trường hợp sốt cao hoặc đang mắc c&aacute;c bệnh cấp t&iacute;nh.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Đ&aacute;p ứng miễn dịch của vắc xin uốn v&aacute;n hấp phụ (TT) c&oacute; thể bị giảm nếu đang d&ugrave;ng c&aacute;c liệu ph&aacute;p ức chế miễn dịch.</li>\r\n	<li>Nếu ti&ecirc;m nhầm vắc xin uốn v&aacute;n hấp phụ (TT) dưới da th&igrave; c&aacute;c phản ứng phụ sẽ rất rầm rộ do vắc xin c&oacute; chứa muối nh&ocirc;m.</li>\r\n	<li>Kh&ocirc;ng ti&ecirc;m qu&aacute; liều.</li>\r\n	<li>Vắc xin kh&ocirc;ng chống chỉ định cho đối tượng phụ nữ đang cho con b&uacute;.</li>\r\n	<li>Cũng giống như tất cả c&aacute;c loại vắc xin kh&aacute;c phải chuẩn bị đầy đủ c&aacute;c phương tiện y tế v&agrave; thuốc cấp cứu để đề ph&ograve;ng phản ứng phản vệ sau khi ti&ecirc;m.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<p>Một số t&aacute;c dụng kh&ocirc;ng mong muốn c&oacute; thể gặp được ghi nhận sau khi sử dụng vắc xin uốn v&aacute;n hấp phụ (TT) như:</p>\r\n\r\n<ul>\r\n	<li>Sốt; đau, sưng, đỏ chỗ ti&ecirc;m. Tuy nhi&ecirc;n c&aacute;c triệu chứng n&agrave;y thường nhẹ v&agrave; tự mất đi</li>\r\n	<li>Sưng hạch bạch huyết gần nơi ti&ecirc;m.</li>\r\n	<li>C&oacute; thể c&oacute; c&aacute;c phản ứng to&agrave;n th&acirc;n: Dị ứng; đau đầu; đổ mồ h&ocirc;i; ớn lạnh; đau cơ, đau khớp.</li>\r\n	<li>Hiếm gặp: Rối loạn chức năng thần kinh c&aacute;nh tay, bả vai.</li>\r\n	<li>Phải th&ocirc;ng b&aacute;o ngay cho b&aacute;c sĩ những t&aacute;c dụng kh&ocirc;ng mong muốn gặp phải sau khi ti&ecirc;m vắc xin.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin được bảo quản ở nhiệt độ từ 2 đến 8 độ C. Kh&ocirc;ng được để đ&ocirc;ng đ&aacute; vắc xin. Loại bỏ vắc xin nếu bị đ&ocirc;ng đ&aacute;.</li>\r\n</ul>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Chỉ định ph&ograve;ng bệnh Uốn v&aacute;n cho người lớn v&agrave; trẻ em. Đặc biệt l&agrave; c&aacute;c đối tượng c&oacute; nguy cơ cao như:</p>\r\n\r\n<ul>\r\n	<li>Phụ nữ c&oacute; thai.</li>\r\n	<li>C&ocirc;ng nh&acirc;n vệ sinh m&ocirc;i trường, cống r&atilde;nh, nước thải.</li>\r\n	<li>Người thường xuy&ecirc;n l&agrave;m việc tại chuồng trại chăn nu&ocirc;i gia s&uacute;c, gia cầm.</li>\r\n	<li>Người l&agrave;m vườn, người l&agrave;m việc ở c&aacute;c trang trại, n&ocirc;ng trường.</li>\r\n	<li>C&ocirc;ng nh&acirc;n x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh.</li>\r\n	<li>Bộ đội v&agrave; thanh ni&ecirc;n xung phong.</li>\r\n</ul>\r\n\r\n<h2>3. Phác đ&ocirc;̀, lịch ti&ecirc;m</h2>\r\n\r\n<p><em><strong>a. Lịch ti&ecirc;m cơ bản 3 mũi</strong></em></p>\r\n\r\n<ul>\r\n	<li>&nbsp;Mũi 1: l&acirc;̀n ti&ecirc;m đầu ti&ecirc;n</li>\r\n	<li>&nbsp;Mũi 2: 1 tháng sau mũi 1</li>\r\n	<li>&nbsp;Mũi 3: 6 tháng sau mũi 2</li>\r\n</ul>\r\n\r\n<p>Lưu ý: Ti&ecirc;m nhắc mỗi 5 &ndash; 10 năm</p>\r\n\r\n<p><em><strong>b. Lịch ti&ecirc;m ở phụ nữ mang thai:</strong></em></p>\r\n\r\n<ul>\r\n	<li>&nbsp;Lần mang thai đầu ti&ecirc;n: ti&ecirc;m 2 mũi</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Mũi 1: ti&ecirc;m sớm khi ph&aacute;t hiện c&oacute; thai &ndash; thường ti&ecirc;m v&agrave;o 3 th&aacute;ng giữa thai kỳ</li>\r\n	<li>Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất một th&aacute;ng, y&ecirc;u cầu trước ng&agrave;y dự sinh &iacute;t nhất 1 th&aacute;ng</li>\r\n	<li>Lần mang thai thứ 2, 3, 4: ti&ecirc;m 01 mũi &ndash; y&ecirc;u cầu trước ng&agrave;y dự sinh &iacute;t nhất 1 th&aacute;ng.</li>\r\n</ul>\r\n\r\n<p><em><strong>c.&nbsp;</strong></em><em><strong>Lịch ti&ecirc;m Kh&aacute;ch h&agrave;ng đang bị phơi nhiễm</strong>&nbsp;</em>(Cần khai th&aacute;c trước đ&oacute; KH c&oacute; ti&ecirc;m mũi Uốn v&aacute;n n&agrave;o chưa)</p>\r\n\r\n<ul>\r\n	<li>&nbsp;Nếu đ&atilde; ti&ecirc;m đủ 5 mũi: Kh&ocirc;ng cần ti&ecirc;m th&ecirc;m</li>\r\n	<li>&nbsp;Nếu đ&atilde; ti&ecirc;m từ 3 mũi trở l&ecirc;n: Ti&ecirc;m th&ecirc;m vắc xin uốn v&aacute;n hấp phụ (TT), lưu &yacute; kh&ocirc;ng ti&ecirc;m huyết thanh (SAT).</li>\r\n	<li>Nếu đ&atilde; ti&ecirc;m &lt; 3 mũi: Ti&ecirc;m tiếp cho đủ 5 mũi.</li>\r\n</ul>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<p>Cần ti&ecirc;m vắc xin Uốn v&aacute;n c&aacute;ch c&aacute;c vắc xin như: 6in1/5in1/4in1/3in1/Td &iacute;t nhất l&agrave; 1 th&aacute;ng ạ.</p>\r\n\r\n<p>Ở phụ nữ mang thai:</p>\r\n\r\n<p>+ Lần mang thai đầu ti&ecirc;n: ti&ecirc;m 2 mũi</p>\r\n\r\n<ul>\r\n	<li>Mũi 1: ti&ecirc;m sớm khi ph&aacute;t hiện c&oacute; thai &ndash; thường ti&ecirc;m v&agrave;o 3 th&aacute;ng giữa thai kỳ.</li>\r\n	<li>Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất một th&aacute;ng, y&ecirc;u cầu trước ng&agrave;y dự sinh &iacute;t nhất 1 th&aacute;ng.</li>\r\n</ul>\r\n\r\n<p>+ Lần mang thai thứ 2, 3, 4: ti&ecirc;m 01 mũi &ndash; y&ecirc;u cầu trước ng&agrave;y dự sinh &iacute;t nhất 1 th&aacute;ng.</p>\r\n\r\n<h2>5. Khoảng c&aacute;ch với vắc xin kh&aacute;c</h2>\r\n\r\n<p>Khoảng c&aacute;ch khi ti&ecirc;m vắc xin uốn v&aacute;n với c&aacute;c vắc xin như 6in1/5in1/4in1/3in1 l&agrave; &iacute;t nhất l&agrave; 1 th&aacute;ng.</p>\r\n\r\n<h2>6. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p>Sau khi ti&ecirc;m vắc xin ph&ograve;ng uốn v&aacute;n hấp phụ (TT), người ti&ecirc;m hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể, cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đau, quầng đỏ, nốt cứng hay sưng xuất hiện trong v&ograve;ng 48 giờ sau khi ti&ecirc;m v&agrave; k&eacute;o d&agrave;i trong 1-2 ng&agrave;y. Tuy nhi&ecirc;n c&aacute;c triệu chứng n&agrave;y thường nhẹ v&agrave; tự mất đi.</li>\r\n	<li>To&agrave;n th&acirc;n: sốt, đau đầu, đổ mồ h&ocirc;i, ớn lạnh, đau cơ, đau khớp.</li>\r\n</ul>', 'ncc4', '19', 3, '30', 1, 'images/hinhanhvaccine/20240626_162721.jpg'),
('vcVắcxinGardasi', 'Vắc xin Gardasil 9', 'Hoa Kỳ', '2950000', 30, '<p><strong>Vắc xin thế hệ mới Gardasil 9</strong>&nbsp;được xem l&agrave; vắc xin b&igrave;nh đẳng giới v&igrave; mở r&ocirc;̣ng cả đ&ocirc;́i tượng và phạm vi phòng b&ecirc;̣nh rộng hơn ở nam v&agrave; nữ giới, bảo vệ khỏi 9 tu&yacute;p virus HPV phổ biến 6, 11, 16, 18, 31, 33, 45, 52 v&agrave; 58 g&acirc;y bệnh ung thư c&ocirc;̉ tử cung, ung thư &acirc;m hộ, ung thư &acirc;m đạo, ung thư h&acirc;̣u m&ocirc;n, ung thư hầu họng, mụn cóc sinh dục, c&aacute;c tổn thương tiền ung thư hoặc loạn sản&hellip;, với hi&ecirc;̣u quả bảo v&ecirc;̣ l&ecirc;n đến tr&ecirc;n 90%.</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p><strong>Vắc xin Gardasil 9</strong>&nbsp;được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học &ndash;&nbsp;<a href=\"https://www.merckvaccines.com/gardasil9/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Merck Sharp &amp; Dohme (MSD &ndash; Mỹ)</a>.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin Gardasil 9 được chỉ định ti&ecirc;m bắp. Vị tr&iacute; ph&ugrave; hợp l&agrave; v&ugrave;ng cơ delta của phần tr&ecirc;n c&aacute;nh tay hoặc ở v&ugrave;ng trước ph&iacute;a tr&ecirc;n đ&ugrave;i.</li>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m Gardasil 9 v&agrave;o mạch m&aacute;u, ti&ecirc;m dưới da hoặc ti&ecirc;m trong da.</li>\r\n	<li>Kh&ocirc;ng được trộn lẫn vắc xin trong c&ugrave;ng một ống ti&ecirc;m với bất kỳ loại vắc xin v&agrave; dung dịch n&agrave;o kh&aacute;c.</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Người qu&aacute; mẫn với c&aacute;c hoạt chất hoặc với bất kỳ t&aacute; dược n&agrave;o của vắc xin được liệt k&ecirc; trong phần &ldquo;Th&agrave;nh phần&rdquo;.</li>\r\n	<li>Những người bị qu&aacute; mẫn sau khi ti&ecirc;m Gardasil 9 hoặc Gardasil trước đ&acirc;y kh&ocirc;ng n&ecirc;n ti&ecirc;m Gardasil 9.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<p>Ho&atilde;n ti&ecirc;m chủng ở những người đang bị sốt nặng cấp t&iacute;nh. Tuy nhi&ecirc;n, t&igrave;nh trạng nhiễm tr&ugrave;ng nhẹ, như nhiễm tr&ugrave;ng đường h&ocirc; hấp tr&ecirc;n nhẹ hoặc sốt mức độ nhẹ kh&ocirc;ng chống chỉ định ti&ecirc;m Gardasil 9.</p>\r\n\r\n<p>Vắc xin chỉ d&ugrave;ng để dự ph&ograve;ng, kh&ocirc;ng c&oacute; t&aacute;c dụng điều trị. Do đ&oacute;,&nbsp;<strong>vắc xin Gardasil 9</strong>&nbsp;kh&ocirc;ng chỉ định để điều trị ung thư cổ tử cung, &acirc;m hộ, &acirc;m đạo v&agrave; hậu m&ocirc;n, tổn thương tiền ung thư hoặc loạn sản cổ tử cung, &acirc;m hộ, &acirc;m đạo v&agrave; hậu m&ocirc;n hoặc mụn c&oacute;c sinh dục mức độ cao&hellip;, kh&ocirc;ng ngăn ngừa c&aacute;c tổn thương do tu&yacute;p HPV c&oacute; trong vắc xin ở những người bị nhiễm tu&yacute;p HPV đ&oacute; tại thời điểm ti&ecirc;m chủng. Vắc xin n&agrave;y n&ecirc;n được d&ugrave;ng thận trọng cho những người bị giảm tiểu cầu hoặc c&oacute; bất kỳ rối loạn đ&ocirc;ng m&aacute;u n&agrave;o v&igrave; chảy m&aacute;u c&oacute; thể xảy ra sau khi ti&ecirc;m bắp ở những người n&agrave;y.</p>\r\n\r\n<p>C&aacute;c nghi&ecirc;n cứu theo d&otilde;i d&agrave;i hạn hiện đang được tiến h&agrave;nh để x&aacute;c định thời gian bảo vệ. Kh&ocirc;ng c&oacute; dữ liệu về độ an to&agrave;n, t&iacute;nh sinh miễn dịch hoặc hiệu quả để hỗ trợ cho việc sử dụng thay thế cho nhau giữa Gardasil 9 với vắc xin HPV nhị gi&aacute; hoặc tứ gi&aacute;.</p>\r\n\r\n<h3><strong>Khả năng sinh sản, phụ nữ c&oacute; thai v&agrave; cho con b&uacute;</strong></h3>\r\n\r\n<h4><strong>1. Phụ nữ c&oacute; thai</strong></h4>\r\n\r\n<p>Dữ liệu về phụ nữ c&oacute; thai cho thấy kh&ocirc;ng c&oacute; biểu hiện dị dạng cũng kh&ocirc;ng c&oacute; độc t&iacute;nh đối với thai/trẻ sơ sinh do Gardasil 9.</p>\r\n\r\n<p>C&aacute;c nghi&ecirc;n cứu tr&ecirc;n động vật kh&ocirc;ng cho thấy độc t&iacute;nh đối với sinh sản. Tuy nhi&ecirc;n, những dữ liệu n&agrave;y được xem l&agrave; kh&ocirc;ng đủ để khuyến c&aacute;o sử dụng Gardasil 9 trong thời kỳ mang thai.</p>\r\n\r\n<p>N&ecirc;n ho&atilde;n việc ti&ecirc;m chủng cho đến khi kết th&uacute;c thai kỳ.</p>\r\n\r\n<h4><strong>2. Phụ nữ cho con b&uacute;</strong></h4>\r\n\r\n<p><strong>Vắc xin Gardasil 9</strong>&nbsp;c&oacute; thể được sử dụng trong thời kỳ cho con b&uacute;.</p>\r\n\r\n<h4><strong>3. Khả năng sinh sản</strong></h4>\r\n\r\n<p>Kh&ocirc;ng c&oacute; dữ liệu về ảnh hưởng của Gardasil 9 đến khả năng sinh sản ở người. C&aacute;c nghi&ecirc;n cứu tr&ecirc;n động vật kh&ocirc;ng cho thấy ảnh hưởng c&oacute; hại đến khả năng sinh sản.</p>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<p>T&iacute;nh an to&agrave;n v&agrave; t&iacute;nh sinh miễn dịch ở những người đ&atilde; nhận được globulin miễn dịch hoặc c&aacute;c sản phẩm c&oacute; nguồn gốc từ m&aacute;u trong 3 th&aacute;ng trước khi ti&ecirc;m chủng chưa được nghi&ecirc;n cứu trong c&aacute;c thử nghiệm l&acirc;m s&agrave;ng.</p>\r\n\r\n<h4><strong>1. Sử dụng với c&aacute;c vắc xin kh&aacute;c Gardasil 9</strong></h4>\r\n\r\n<p>Vắc xin Gardasil 9 c&oacute; thể được d&ugrave;ng đồng thời với một vắc xin tăng cường kết hợp chứa Bạch hầu (d) v&agrave; uốn v&aacute;n (T) c&ugrave;ng với ho g&agrave; [th&agrave;nh phần v&ocirc; b&agrave;o] v&agrave;/hoặc bại liệt [bất hoạt] (IPV) (vắc xin dTap, dT-IPV, dTap-IPV) v&agrave; kh&ocirc;ng c&oacute; sự can thiệp đ&aacute;ng kể v&agrave;o đ&aacute;p ứng kh&aacute;ng thể đối với bất kỳ th&agrave;nh phần n&agrave;o của một trong hai vắc xin. Điều n&agrave;y dựa tr&ecirc;n c&aacute;c kết quả từ một thử nghiệm l&acirc;m s&agrave;ng, trong đ&oacute; vắc xin dTap-IPV kết hợp được sử dụng đồng thời với liều Gardasil 9 đầu ti&ecirc;n.</p>\r\n\r\n<h4><strong>2. Sử dụng với thuốc tr&aacute;nh thai chứa hormone</strong></h4>\r\n\r\n<p>Trong c&aacute;c nghi&ecirc;n cứu l&acirc;m s&agrave;ng, 60,2% phụ nữ từ 16-26 tuổi&nbsp;<em>ti&ecirc;m Gardasil 9</em>&nbsp;đ&atilde; sử dụng thuốc tr&aacute;nh thai chứa hormone trong thời gian ti&ecirc;m chủng ở c&aacute;c nghi&ecirc;n cứu l&acirc;m s&agrave;ng. Sử dụng thuốc tr&aacute;nh thai chứa hormone dường như kh&ocirc;ng ảnh hưởng đến đ&aacute;p ứng miễn dịch đặc hiệu tu&yacute;p đối với Gardasil 9.</p>\r\n\r\n<h3><strong>Tương kỵ</strong></h3>\r\n\r\n<p>Kh&ocirc;ng c&oacute; nghi&ecirc;n cứu về sự tương hợp, kh&ocirc;ng được trộn lẫn thuốc n&agrave;y với c&aacute;c thuốc kh&aacute;c.</p>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Phản ứng tại chỗ ti&ecirc;m: Đau, sưng, nổi ban đỏ, chai cứng, ngứa, bầm t&iacute;m, tăng nhạy cảm tại chỗ ti&ecirc;m.</li>\r\n	<li>C&aacute;c phản ứng to&agrave;n th&acirc;n kh&aacute;c: Sốt, nhức đầu, mệt mỏi, ch&oacute;ng mặt, buồn n&ocirc;n.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<p><strong>Vắc xin Gardasil 9</strong>&nbsp;được bảo quản trong tủ lạnh (2&deg;C &ndash; 8&deg;C). Kh&ocirc;ng để đ&ocirc;ng lạnh.</p>\r\n\r\n<p>Giữ ống ti&ecirc;m chứa sẵn vắc xin trong hộp carton ngo&agrave;i để tr&aacute;nh &aacute;nh s&aacute;ng. N&ecirc;n sử dụng Gardasil 9 c&agrave;ng sớm c&agrave;ng tốt sau khi được lấy ra khỏi tủ lạnh.</p>\r\n\r\n<p><em>Người từ tr&ograve;n 9 tuổi đến dưới 15 tuổi tại thời điểm ti&ecirc;m lần đầu ti&ecirc;n:</em></p>\r\n\r\n<ul>\r\n	<li>Ph&aacute;c đồ 2 mũi:\r\n	<ul>\r\n		<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n trong độ tuổi</li>\r\n		<li>Mũi 2: c&aacute;ch mũi 1 từ 6-12 th&aacute;ng.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>Nếu mũi 2 ti&ecirc;m c&aacute;ch mũi 1 &lt; 5 th&aacute;ng, cần ti&ecirc;m mũi 3 c&aacute;ch mũi 2 &iacute;t nhất 3 th&aacute;ng.</p>\r\n\r\n<ul>\r\n	<li>Ph&aacute;c đồ 3 mũi (0-2-6):\r\n	<ul>\r\n		<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n trong độ tuổi</li>\r\n		<li>Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất 2 th&aacute;ng</li>\r\n		<li>Mũi 3: c&aacute;ch mũi 2 &iacute;t nhất 4 th&aacute;ng</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><em>Người từ tr&ograve;n 15 tuổi đến 45 tuổi tại thời điểm ti&ecirc;m lần đầu ti&ecirc;n:</em></p>\r\n\r\n<ul>\r\n	<li>Ph&aacute;c đồ 3 mũi (0-2-6):\r\n	<ul>\r\n		<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n trong độ tuổi</li>\r\n		<li>Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất 2 th&aacute;ng</li>\r\n		<li>Mũi 3: c&aacute;ch mũi 2 &iacute;t nhất 4 th&aacute;ng.</li>\r\n	</ul>\r\n	</li>\r\n	<li>Ph&aacute;c đồ ti&ecirc;m nhanh:\r\n	<ul>\r\n		<li>Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n trong độ tuổi.</li>\r\n		<li>Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất 1 th&aacute;ng</li>\r\n		<li>Mũi 3: c&aacute;ch mũi 2 &iacute;t nhất 3 th&aacute;ng.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Vắc xin Gardasil 9</strong>&nbsp;c&oacute; độ an to&agrave;n cao. Tuy nhi&ecirc;n, người được ti&ecirc;m c&oacute; thể gặp một số phản ứng nhẹ, k&eacute;o d&agrave;i từ 1-2 ng&agrave;y:</p>\r\n\r\n<ul>\r\n	<li>Phản ứng tại chỗ ti&ecirc;m: Nổi ban đỏ, chai cứng, sưng đau, ngứa, tăng nhạy cảm tại chỗ ti&ecirc;m.</li>\r\n	<li>C&aacute;c phản ứng to&agrave;n th&acirc;n kh&aacute;c: Sốt nhẹ, đau đầu, buồn ngủ, giảm cảm gi&aacute;c th&egrave;m ăn, buồn n&ocirc;n, n&ocirc;n mửa, ti&ecirc;u chảy.</li>\r\n</ul>', 'ncc1', '28', 3, '60', 1, 'images/hinhanhvaccine/20240610_062333.jpg'),
('vcVaricella', 'Varicella', 'Hàn Quốc', '700000', 61, '<h2>1. Th&ocirc;ng tin vắc xin</h2>\r\n\r\n<p>Vắc xin Varicella tạo miễn dịch dịch chủ động ph&ograve;ng bệnh thủy đậu do virus Varicella Zoster g&acirc;y ra.</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Varicella được nghi&ecirc;n cứu v&agrave; sản xuất bởi&nbsp;<a href=\"https://greencross.com.ph/\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">Green Cross</a>&nbsp;&ndash; H&agrave;n Quốc.</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin phải được sử dụng ngay kh&ocirc;ng qu&aacute; 30 ph&uacute;t sau khi ho&agrave;n nguy&ecirc;n với nước hồi chỉnh cung cấp.</li>\r\n	<li>Ti&ecirc;m dưới da. Liều đơn 0.5ml</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Qu&aacute; mẫn với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin.</li>\r\n	<li>Kh&ocirc;ng d&ugrave;ng vắc xin cho đối tượng đang sốt hoặc suy dinh dưỡng.</li>\r\n	<li>Bệnh tim mạch, rối loạn chức năng gan, thận.</li>\r\n	<li>C&oacute; tiền sử qu&aacute; mẫn với kanamycin v&agrave; Erythromycin.</li>\r\n	<li>C&oacute; tiền sử co giật trong v&ograve;ng 1 năm trước khi ti&ecirc;m vắc xin.</li>\r\n	<li>Suy giảm miễn dịch tế b&agrave;o.</li>\r\n	<li>C&oacute; thai hoặc 2 th&aacute;ng trước khi định c&oacute; thai.</li>\r\n	<li>Đ&atilde; ti&ecirc;m ph&ograve;ng vắc xin sống kh&aacute;c trong v&ograve;ng 1 th&aacute;ng gần đ&acirc;y (Sởi, quai bị, rubella, lao, bại liệt).</li>\r\n	<li>Suy giảm miễn dịch ti&ecirc;n ph&aacute;t hoặc mắc phải như AIDS hoặc c&aacute;c biểu hiện l&acirc;m s&agrave;ng của nhiễm virus g&acirc;y suy giảm miễn dịch ở người.</li>\r\n	<li>Trẻ em dưới 12 th&aacute;ng tuổi.</li>\r\n	<li>Bệnh nh&acirc;n mắc bệnh bạch cầu tủy cấp, bệnh bạch cầu tế b&agrave;o lympho T hoặc u lympho &aacute;c t&iacute;nh.</li>\r\n	<li>Bệnh nh&acirc;n bị ức chế mạnh hệ thống miễn dịch do xạ trị hoặc giai đoạn tấn c&ocirc;ng trong điều trị bệnh bạch cầu.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Điều trị liệu ph&aacute;p ph&ugrave; hợp bằng epinephrine ngay khi phản ứng qu&aacute; mẫn xảy ra.</li>\r\n	<li>Khoảng thời gian để c&oacute; hiệu quả bảo vệ sau khi ti&ecirc;m vắc xin Varicella chưa được biết r&otilde;. Tuy nhi&ecirc;n sau khi ti&ecirc;m vắc xin cần tr&aacute;nh tiếp x&uacute;c với người c&oacute; nguy cơ mắc bệnh cao trong v&ograve;ng 6 tuần. C&aacute;c đối tượng c&oacute; nguy cơ cao đ&oacute; l&agrave; c&aacute;c đối tượng suy giảm miễn dịch, phụ nữ c&oacute; thai kh&ocirc;ng c&oacute; tiền sử mắc bệnh hoặc x&eacute;t nghiệm kh&ocirc;ng thấy sự nhiễm bệnh trước đ&oacute;.</li>\r\n	<li>Cần thận trọng đối với c&aacute;c đối tượng phụ nữ cho con b&uacute; d&ugrave; chưa r&otilde; virus thủy đậu c&oacute; thể b&agrave;i tiết qua sữa mẹ hay kh&ocirc;ng.</li>\r\n	<li>Trong trường hợp đối tượng điều trị bằng 6-mercaptopurine, phải ngừng thuốc &iacute;t nhất 1 tuần trước khi ti&ecirc;m vắc xin v&agrave; sử dụng lại &iacute;t nhất 1 tuần sau đ&oacute;.</li>\r\n	<li>Trong trường hợp khẩn cấp (phải tạo miễn dịch chủ động bằng c&aacute;ch ti&ecirc;m immunoglobulin Varicella &ndash; zoster), ti&ecirc;m ph&ograve;ng vắc xin cần được tiến h&agrave;nh trừ khi c&oacute; triệu chứng suy giảm miễn dịch. Trong trường hợp khẩn cấp n&agrave;y vắc xin cần phải được ti&ecirc;m trong v&ograve;ng 72 giờ sau khi phơi nhiễm với virus Varicella.</li>\r\n</ul>\r\n\r\n<h3><strong>Tương t&aacute;c thuốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng ti&ecirc;m vắc xin &iacute;t nhất 5 th&aacute;ng sau khi truyền m&aacute;u hoặc huyết tương v&agrave; ti&ecirc;m bất kỳ một immunoglobulin n&agrave;o hoặc immunoglobulin với Varicella Zoster (VZIG).</li>\r\n	<li>Sau khi ti&ecirc;m vắc xin trong v&ograve;ng 2 th&aacute;ng kh&ocirc;ng được sử dụng immunoglobulin kể cả VZIG trừ khi c&aacute;c chế phẩm n&agrave;y c&oacute; lợi hơn ti&ecirc;m vắc xin.</li>\r\n	<li>Trong v&ograve;ng 6 tuần sau khi ti&ecirc;m vắc xin kh&ocirc;ng được sử dụng salicylate do hội chứng Reye đ&atilde; xảy ra ở những bệnh nh&acirc;n mắc thủy đậu một c&aacute;ch tự nhi&ecirc;n được điều trị bằng salicylate.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Ở bệnh nh&acirc;n c&oacute; nguy cơ cao, sau 14 -30 ng&agrave;y sau khi ti&ecirc;m vắc xin c&oacute; thể xuất hiện c&aacute;c nốt ph&aacute;t ban dạng nốt sần hoặc phỏng nước k&egrave;m theo sốt nhẹ. Gần 20% bệnh nh&acirc;n bệnh bạch cầu lympho cấp t&iacute;nh c&oacute; phản ứng phụ n&agrave;y.</li>\r\n	<li>C&aacute;c biểu hiện của bệnh Herpes zoster cũng c&oacute; thể xuất hiện ở bệnh nh&acirc;n c&oacute; nguy cơ cao.</li>\r\n	<li>C&aacute;c phản ứng thường gặp như: Sưng đau, tấy đỏ, nổi ban, ngứa, tụ m&aacute;u, nổi mảng cứng chỗ ti&ecirc;m; sốt cao tr&ecirc;n 39 độ C; ph&aacute;t ban dạng thủy đậu (to&agrave;n th&acirc;n hoặc chỗ ti&ecirc;m).</li>\r\n	<li>Phải th&ocirc;ng b&aacute;o ngay cho b&aacute;c sĩ những t&aacute;c dụng kh&ocirc;ng mong muốn gặp phải sau khi ti&ecirc;m vắc xin.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<p>Vắc xin được bảo quản ở nhiệt độ từ 2 độ C đến 8 độ C. Tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp.</p>\r\n\r\n<h2>2. Đối tượng</h2>\r\n\r\n<p>Vắc xin Varicella l&agrave; vắc xin dạng đ&ocirc;ng kh&ocirc; của virus thủy đậu (varicella) sống giảm độc lực, được chỉ định ph&ograve;ng thủy đậu cho trẻ từ 12 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người lớn.</p>\r\n\r\n<h2>3. Ph&aacute;c đồ, lịch ti&ecirc;m</h2>\r\n\r\n<p><strong><em>* Trẻ từ 12 th&aacute;ng đến 12 tuổi c&oacute; lịch ti&ecirc;m 2 mũi:</em></strong></p>\r\n\r\n<p>&ndash; Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</p>\r\n\r\n<p>&ndash; Mũi 2: 3 th&aacute;ng sau mũi 1 (ưu ti&ecirc;n) hoặc hẹn mũi 2 l&uacute;c 4-6 tuổi.</p>\r\n\r\n<p><strong><em>&nbsp;* Trẻ từ từ 13 tuổi trở l&ecirc;n v&agrave; người lớn c&oacute; lịch ti&ecirc;m 2 mũi:</em></strong></p>\r\n\r\n<p>&ndash; Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</p>\r\n\r\n<p>&ndash; Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất 01 th&aacute;ng.</p>\r\n\r\n<h2>4. Điều kiện trước ti&ecirc;m</h2>\r\n\r\n<p><strong>Khi ti&ecirc;m c&aacute;c vắc xin thủy đậu c&oacute; cần khoảng c&aacute;ch g&igrave; kh&ocirc;ng?</strong></p>\r\n\r\n<p>Để ti&ecirc;m&nbsp;<a href=\"https://vnvc.vn/vac-xin-thuy-dau/\" rel=\"noopener\" target=\"_blank\"><strong>vắc xin thuỷ đậu</strong></a>, Kh&aacute;ch h&agrave;ng cần lưu &yacute; trong v&ograve;ng 1 th&aacute;ng gần nhất kh&ocirc;ng ti&ecirc;m c&aacute;c vắc xin sống giảm độc lực.</p>\r\n\r\n<h2>5. Khoảng c&aacute;ch với vắc xin kh&aacute;c</h2>\r\n\r\n<p>Thực tế c&aacute;c b&aacute;c sĩ thường khuyến c&aacute;o ti&ecirc;m vắc xin Varicella c&aacute;ch khoảng 2 tuần với c&aacute;c vắc xin kh&aacute;c, trừ c&aacute;c vắc xin sống giảm độc lực th&igrave; cần ti&ecirc;m c&aacute;ch vắc xin thủy đậu &iacute;t nhất 1 th&aacute;ng. Trước khi ti&ecirc;m, c&aacute;c b&aacute;c sĩ sẽ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp.</p>\r\n\r\n<h2>6. Phản ứng sau ti&ecirc;m chủng</h2>\r\n\r\n<p><strong>Sau khi ti&ecirc;m vắc xin thủy đậu về th&igrave; b&eacute; c&oacute; sốt kh&ocirc;ng?</strong></p>\r\n\r\n<p>Sau khi ti&ecirc;m vắc xin ph&ograve;ng bệnh thủy đậu, người ti&ecirc;m hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể. Cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: ph&aacute;t ban dạng thủy đậu, đau, đỏ, sưng.</li>\r\n	<li>To&agrave;n th&acirc;n: sốt</li>\r\n</ul>\r\n\r\n<p>Lưu &yacute;: Cần tr&aacute;nh d&ugrave;ng chế phẩm chứa Salicylate (Aspirin hoặc c&aacute;c chế phẩm b&ocirc;i, d&aacute;n giảm đau) trong &iacute;t nhất 6 tuần sau khi ti&ecirc;m.</p>', 'ncc4', '15', 2, '90', 1, 'images/hinhanhvaccine/20240626_164302.jpg'),
('vcVaxigripTetra', 'Vaxigrip Tetra', 'Pháp', '356000', 0, '<p>Vắc xin c&uacute;m Tứ gi&aacute; Vaxigrip Tetra ph&ograve;ng được 4 chủng tu&yacute;p virus c&uacute;m gồm: 2 chủng c&uacute;m A (H1N1, H3N2) v&agrave; 2 chủng c&uacute;m B (Yamagata, Victoria).</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<p>Vắc xin Vaxigrip Tetra được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học Sanofi Pasteur (Ph&aacute;p).</p>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<p>Ti&ecirc;m bắp hoặc ti&ecirc;m dưới da.</p>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Qu&aacute; mẫn cảm với c&aacute;c hoạt chất, với bất kỳ t&aacute; dược liệt k&ecirc; trong mục &ldquo;th&agrave;nh phần&rdquo; hoặc bất kỳ chất n&agrave;o c&oacute; thể c&oacute; trong th&agrave;nh phần d&ugrave; với một lượng rất nhỏ c&ograve;n s&oacute;t lại (vết) như trứng (<a href=\"https://www.sciencedirect.com/topics/agricultural-and-biological-sciences/ovalbumin\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">ovalbumin</a>, protein của g&agrave;), neomycin, formaldehyde v&agrave; octoxynol-9.</li>\r\n	<li>Ho&atilde;n ti&ecirc;m vắc xin với những người bị sốt vừa hay sốt cao hay bị bệnh cấp t&iacute;nh.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m Vaxigrip Tetra v&agrave;o tĩnh mạch.</li>\r\n	<li>Thận trọng khi sử dụng cho người bị suy giảm miễn dịch, suy giảm tiểu cầu hoặc bị rối loạn chảy m&aacute;u.</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Phản ứng tại chỗ: ban đỏ (quầng đỏ), sưng, đau, bầm m&aacute;u, nốt cứng.</li>\r\n	<li>Phản ứng to&agrave;n th&acirc;n: sốt, kh&oacute; chịu, run rẩy, mệt mỏi, đau đầu, đổ mồ h&ocirc;i, đau khớp v&agrave; đau cơ.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<p>Vắc xin Vaxigrip Tetra được bảo quản ở nhiệt độ 2-8 độ C. Kh&ocirc;ng để đ&ocirc;ng băng v&agrave; tr&aacute;nh &aacute;nh s&aacute;ng.</p>\r\n\r\n<p>Vắc xin Vaxigrip Tetra 0.5ml của Ph&aacute;p ph&ograve;ng c&uacute;m m&ugrave;a, được chỉ định cho trẻ từ 6 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người lớn.</p>\r\n\r\n<p><em>Vắc xin&nbsp;Vaxigrip Tetra 0,5 ml&nbsp;d&agrave;nh cho trẻ từ 6 th&aacute;ng tuổi đến dưới 9 tuổi chưa ti&ecirc;m c&uacute;m c&oacute; lịch ti&ecirc;m 2 mũi:</em></p>\r\n\r\n<ul>\r\n	<li>&nbsp;Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</li>\r\n	<li>&nbsp;Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất 4 tuần v&agrave; ti&ecirc;m nhắc h&agrave;ng năm.</li>\r\n</ul>\r\n\r\n<p>Từ 9 tuổi trở l&ecirc;n: Lịch ti&ecirc;m 01 mũi duy nhất v&agrave; nhắc lại hằng năm.</p>\r\n\r\n<p>Tổ chức Y tế Thế giới (WHO) khuyến c&aacute;o chỉ cần ti&ecirc;m nhắc lại vắc xin c&uacute;m h&agrave;ng năm, lu&ocirc;n c&aacute;ch tối thiểu 12 th&aacute;ng so với mũi trước.</p>', 'ncc6', '12', 2, '30', 1, 'images/hinhanhvaccine/1717998669.jpg'),
('vcVerorab', 'Verorab', 'Pháp', '495000', 99, '<h2><strong>1. Th&ocirc;ng tin vắc xin</strong></h2>\r\n\r\n<p>Vắc xin Verorab c&oacute; t&aacute;c dụng tạo miễn dịch chủ động ph&ograve;ng bệnh dại cho cả người lớn v&agrave; trẻ em, sau khi tiếp x&uacute;c hoặc bị con vật nghi bị dại cắn.</p>\r\n\r\n<h3><strong>Nguồn gốc</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin Verorab l&agrave;&nbsp;<a href=\"https://vnvc.vn/vac-xin-phong-dai/\" rel=\"noopener\" target=\"_blank\">vắc xin ph&ograve;ng dại</a>&nbsp;cho trẻ em v&agrave; người lớn, do&nbsp;tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học&nbsp;Sanofi Pasteur (Ph&aacute;p) sản xuất.</li>\r\n</ul>\r\n\r\n<h3><strong>Đường ti&ecirc;m</strong></h3>\r\n\r\n<ul>\r\n	<li>Ti&ecirc;m bắp: với liều 0.5ml vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n, ở người lớn v&agrave;o v&ugrave;ng cơ Delta ở c&aacute;nh tay, trẻ nhỏ ti&ecirc;m ở mặt trước &ndash; b&ecirc;n đ&ugrave;i. Kh&ocirc;ng ti&ecirc;m ở v&ugrave;ng m&ocirc;ng.</li>\r\n	<li>Ti&ecirc;m trong da: với liều 0.1ml vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n (bằng 1/5 liều ti&ecirc;m bắp).</li>\r\n</ul>\r\n\r\n<h3><strong>Chống chỉ định</strong></h3>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m trong da ở những trường hợp sau: đang điều trị d&agrave;i ng&agrave;y bằng c&aacute;c thuốc ức chế miễn dịch (bao gồm cả corticoid), v&agrave;&nbsp;<a href=\"https://www.webmd.com/drugs/2/drug-8633/chloroquine-oral/details\" rel=\"nofollow noreferrer noopener\" target=\"_blank\">thuốc Chloroquin</a>; người bị khiếm khuyết miễn dịch; trẻ em hoặc người c&oacute; vết cắn nặng phần đầu, cổ, hay đến kh&aacute;m trễ sau khi bị vết thương.</li>\r\n</ul>\r\n\r\n<h3><strong>Thận trọng khi sử dụng</strong></h3>\r\n\r\n<ul>\r\n	<li>Người dị ứng với neomycin. Kh&ocirc;ng ti&ecirc;m c&ugrave;ng vị tr&iacute; hay chung bơm ti&ecirc;m vắc xin v&agrave; immunoglobulin.</li>\r\n	<li>Kh&ocirc;ng được ti&ecirc;m vắc xin v&agrave;o trong l&ograve;ng mạch.</li>\r\n	<li>Phụ nữ c&oacute; thai: hiện chưa c&oacute; c&aacute;c bằng chứng Verorab g&acirc;y ảnh hưởng đến thai nhi.&nbsp;Ch&iacute;nh v&igrave; thế, nếu thai phụ bị ch&oacute; dại cắn, cần đến ngay c&aacute;c cơ sở y tế để được kh&aacute;m, tư vấn v&agrave; ti&ecirc;m vắc xin ph&ograve;ng dại kịp thời. Đ&acirc;y l&agrave; sự lựa chọn duy nhất để ph&ograve;ng bệnh dại ph&aacute;t cơn v&agrave; g&acirc;y nguy hiểm đến t&iacute;nh mạng mẹ bầu.</li>\r\n	<li>Phụ nữ cho con b&uacute;: bệnh dại l&agrave; bệnh chết người v&igrave; vậy kh&ocirc;ng c&oacute; hạn chế n&agrave;o tr&ecirc;n phụ nữ cho con b&uacute;.</li>\r\n	<li>Sử dụng ở trẻ em: bệnh dại l&agrave; bệnh chết người, kh&ocirc;ng c&oacute; giới hạn tuổi t&aacute;c v&agrave; được sử dụng cho trẻ em ở mọi lứa tuổi</li>\r\n</ul>\r\n\r\n<h3><strong>T&aacute;c dụng kh&ocirc;ng mong muốn</strong></h3>\r\n\r\n<ul>\r\n	<li>Phản ứng tại chỗ ti&ecirc;m: sưng, đau, đỏ da.</li>\r\n	<li>Phản ứng to&agrave;n th&acirc;n: mệt mỏi, đau đầu, c&oacute; thể sốt, run rẩy, ngất. C&oacute; thể đau nhức xương khớp, đau cơ. Rối loạn dạ d&agrave;y, ruột.</li>\r\n	<li>Hiếm gặp sốc phản vệ.</li>\r\n</ul>\r\n\r\n<h3><strong>Bảo quản</strong></h3>\r\n\r\n<ul>\r\n	<li>Vắc xin được bảo quản ở nhiệt độ từ 2 đến 8 độ C.</li>\r\n</ul>\r\n\r\n<h2><strong>2. Đối tượng</strong></h2>\r\n\r\n<p>Vắc xin Verorab (Ph&aacute;p) ph&ograve;ng bệnh dại được chỉ định ti&ecirc;m cho&nbsp;trẻ em v&agrave; người lớn ở mọi lứa tuổi.</p>\r\n\r\n<h2><strong>3. Phác đ&ocirc;̀, lịch ti&ecirc;m</strong></h2>\r\n\r\n<p>Lịch ti&ecirc;m vắc xin dại dự ph&ograve;ng trước phơi nhiễm gồm 3 mũi: V&agrave;o c&aacute;c ng&agrave;y 0-7-21 hoặc (28).</p>\r\n\r\n<p>Lịch ti&ecirc;m khi xác định có phơi nhi&ecirc;̃m:</p>\r\n\r\n<ul>\r\n	<li><em>Người chưa ti&ecirc;m dự ph&ograve;ng:</em></li>\r\n</ul>\r\n\r\n<p>Ti&ecirc;m 4 mũi (*): v&agrave;o c&aacute;c ng&agrave;y N0 &ndash; N3 &ndash; N7 &ndash; N28</p>\r\n\r\n<p>Ti&ecirc;m 5 mũi (**) v&agrave;o c&aacute;c ng&agrave;y N0 &ndash; N3 &ndash; N7- N14 &ndash; N28</p>\r\n\r\n<p>Lưu &yacute;:</p>\r\n\r\n<p>(*) Con vật sống sau 10 ng&agrave;y theo d&otilde;i</p>\r\n\r\n<p>(**) Con vật chết, bệnh, kh&ocirc;ng theo d&otilde;i được</p>\r\n\r\n<p>Ti&ecirc;m c&agrave;ng sớm c&agrave;ng tốt ngay sau khi bị động vật cắn.</p>\r\n\r\n<p>Kh&ocirc;ng ti&ecirc;m huyết thanh trễ hơn 7 ng&agrave;y sau ng&agrave;y ti&ecirc;m liều đầu ti&ecirc;n vắc xin Dại.</p>\r\n\r\n<ul>\r\n	<li><em>Người đ&atilde; ti&ecirc;m dự ph&ograve;ng trước phơi nhiễm hoặc sau phơi nhiễm &iacute;t nhất 3 mũi vắc xin Dại:</em><br />\r\n	Ti&ecirc;m 2 mũi v&agrave;o c&aacute;c ng&agrave;y 0-3.</li>\r\n</ul>\r\n\r\n<h2><strong>4. Điều kiện trước ti&ecirc;m</strong></h2>\r\n\r\n<p>Vắc xin Dại lu&ocirc;n khuyến c&aacute;o ti&ecirc;m c&agrave;ng sớm c&agrave;ng tốt. Kh&aacute;ch h&agrave;ng n&ecirc;n sắp xếp đến&nbsp;<a href=\"https://vnvc.vn/\" rel=\"noopener\" target=\"_blank\">Trung t&acirc;m VNVC</a>&nbsp;để b&aacute;c sĩ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp.</p>\r\n\r\n<h2><strong>5. Khoảng c&aacute;ch với vắc xin kh&aacute;c</strong></h2>\r\n\r\n<p>Vắc xin Dại kh&ocirc;ng chống chỉ định ti&ecirc;m chung với c&aacute;c loại vắc xin kh&aacute;c, tuy nhi&ecirc;n c&ograve;n phụ thuộc v&agrave;o thể trạng của Kh&aacute;ch h&agrave;ng tại thời điểm ti&ecirc;m chủng b&aacute;c sĩ sẽ thăm kh&aacute;m v&agrave; chỉ định mũi ti&ecirc;m ph&ugrave; hợp.</p>\r\n\r\n<h2><strong>6. Phản ứng sau ti&ecirc;m chủng</strong></h2>\r\n\r\n<p><strong><em>Ti&ecirc;m vắc xin dại về c&oacute; sốt hay c&oacute; triệu chứng g&igrave; kh&ocirc;ng?</em></strong></p>\r\n\r\n<p>Sau khi ti&ecirc;m vắc xin ph&ograve;ng vắc xin dại, người ti&ecirc;m hay gặp những phản ứng nhẹ, kh&ocirc;ng k&eacute;o d&agrave;i v&agrave; kh&ocirc;ng cần điều trị đặc biệt. Đ&oacute; l&agrave; sự đ&aacute;p ứng miễn dịch của cơ thể, cụ thể như sau:</p>\r\n\r\n<ul>\r\n	<li>Tại chỗ ti&ecirc;m: đau, quầng đỏ, sưng, ngứa v&agrave; nốt cứng.</li>\r\n	<li>To&agrave;n th&acirc;n: sốt, đau đầu, ch&oacute;ng mặt, mệt mỏi.</li>\r\n</ul>\r\n\r\n<p>Lưu &yacute;: Khi c&oacute; phơi nhiễm (bị s&uacute;c vật liếm, c&agrave;o, cắn,&hellip;) phải sơ cứu và rửa sạch v&ecirc;́t thương với th&acirc;̣t nhi&ecirc;̀u nước và xà phòng. Sau đó rửa lại th&acirc;̣t kỹ bằng nước sạch. Ti&ecirc;́p tục rửa v&ecirc;́t thương bằng c&ocirc;̀n Iod. Đưa nạn nh&acirc;n tới trung t&acirc;m ti&ecirc;m chủng. T&ugrave;y v&agrave;o mức độ của tiếp x&uacute;c m&agrave; b&aacute;c sĩ c&oacute; thể chỉ định ti&ecirc;m th&ecirc;m globulin miễn dịch.</p>\r\n\r\n<p>N&ecirc;n ti&ecirc;m th&ecirc;m vắc xin uốn v&aacute;n ở tất cả c&aacute;c trường hợp c&oacute; phơi nhiễm.</p>', 'ncc1', '22', 3, '7', 1, 'images/hinhanhvaccine/20240626_155634.jpg');

